'use client';

import React, { useState, useEffect, useCallback } from 'react';
import {
  Edit,
  CheckCircle,
  Circle,
  Clock,
  Target,
  Zap,
  Lightbulb,
  Shield,
  Plus,
  Search,
  Filter,
} from 'lucide-react';
import { Sidebar } from '@/components/ui/Sidebar';
import { UniversalButton } from '@/components/ui/UniversalButton';
import type { PlayerStatus } from '@/components/basketball/PlayerListCard';

// Define Player type locally to match the actual data structure
interface Player {
  id: string;
  name: string;
  team: string;
  status: PlayerStatus;
}
import { z } from 'zod';

// Zod schemas for validation
const DevelopmentGoalSchema = z.object({
  id: z.string(),
  title: z.string(),
  description: z.string(),
  status: z.enum(['not_started', 'in_progress', 'completed']),
  targetDate: z.string(),
  completedDate: z.string().optional(),
});

const DevelopmentPlanSchema = z.object({
  id: z.string(),
  playerId: z.string(),
  playerName: z.string(),
  coachName: z.string().optional(),
  initialObservation: z.string().optional(),
  objective: z.string(),
  description: z.string().optional(),
  status: z.enum(['draft', 'active', 'completed', 'archived']),
  startDate: z.string(),
  endDate: z.string(),
  goals: z.array(DevelopmentGoalSchema),
  tags: z.array(z.string()),
  readiness: z.enum(['high', 'medium', 'low']),
  lastUpdated: z.string().optional(),
  createdAt: z.string(),
  updatedAt: z.string(),
});

// Types for development plans
interface DevelopmentPlan {
  id: string;
  playerId: string;
  playerName: string;
  coachName?: string;
  initialObservation?: string;
  objective: string;
  description?: string;
  status?: 'draft' | 'active' | 'completed' | 'archived';
  startDate?: string;
  endDate?: string | null;
  goals?: DevelopmentGoal[];
  tags?: string[];
  readiness?: 'high' | 'medium' | 'low';
  lastUpdated?: string;
  createdAt?: string;
  updatedAt?: string;
  title?: string; // for UI compatibility
}

interface DevelopmentGoal {
  id: string;
  title: string;
  description: string;
  status: 'not_started' | 'in_progress' | 'completed';
  targetDate: string;
  completedDate?: string;
}

interface DrillSuggestion {
  id: string;
  name: string;
  category:
    | 'shooting'
    | 'ball_handling'
    | 'defense'
    | 'conditioning'
    | 'team_play';
  difficulty: 'beginner' | 'intermediate' | 'advanced';
  duration: number; // minutes
  description: string;
  cues: string[];
  constraints: string[];
  players: number; // min players needed
}

interface Team {
  id: string;
  name: string;
}

export default function DevelopmentPlansPage() {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [plans, setPlans] = useState<DevelopmentPlan[]>([]);

  // Player list state - EXACT SAME AS PLAYERS PAGE
  const [playersById, setPlayersById] = useState<Record<string, Player>>({});
  const [playerIds, setPlayerIds] = useState<string[]>([]);
  const [teams, setTeams] = useState<Team[]>([]);
  const [selectedPlayer, setSelectedPlayer] = useState<Player | null>(null);

  // Infinite scroll state - EXACT SAME AS PLAYERS PAGE
  const [loadingPlayers, setLoadingPlayers] = useState(false);
  const [hasMore, setHasMore] = useState(true);
  const [offset, setOffset] = useState(0);

  // Search and filter state - EXACT SAME AS PLAYERS PAGE
  const [searchTerm, setSearchTerm] = useState('');
  const [teamFilter, setTeamFilter] = useState('all');
  const [isTeamDropdownOpen, setIsTeamDropdownOpen] = useState(false);

  // Handler for selecting a player - EXACT SAME AS PLAYERS PAGE
  const handlePlayerSelect = (player: Player) => {
    setSelectedPlayer(player);
  };

  // Fetch players with pagination - EXACT SAME AS PLAYERS PAGE
  const fetchPlayers = useCallback(
    async (currentOffset: number = 0, reset: boolean = false) => {
      setLoadingPlayers(true);
      try {
        const response = await fetch(
          `/api/dashboard/players?offset=${currentOffset}&limit=10`
        );
        const data = await response.json();

        if (data.players) {
          const transformedPlayers = data.players.map((player: any) => ({
            id: player.id,
            name: player.name || 'Unknown Player',
            team: player.team || 'No Team',
            status: player.status || 'active',
          }));

          if (reset) {
            // Reset the list with normalized state
            const playersMap: Record<string, Player> = {};
            const ids: string[] = [];

            transformedPlayers.forEach((player: Player) => {
              if (!playersMap[player.id]) {
                playersMap[player.id] = player;
                ids.push(player.id);
              }
            });

            setPlayersById(playersMap);
            setPlayerIds(ids);
            setOffset(10);
          } else {
            // Append to existing list with normalized state
            setPlayersById(prevPlayersById => {
              const newPlayersById = { ...prevPlayersById };
              const newIds: string[] = [];

              transformedPlayers.forEach((player: Player) => {
                if (!newPlayersById[player.id]) {
                  newPlayersById[player.id] = player;
                  newIds.push(player.id);
                }
              });

              setPlayerIds(prevIds => [...prevIds, ...newIds]);
              return newPlayersById;
            });
            setOffset(prev => prev + 10);
          }

          setHasMore(data.players.length === 10);
        }
      } catch (error) {
        console.error('Error fetching players:', error);
      } finally {
        setLoadingPlayers(false);
      }
    },
    []
  );

  // Load more players when scrolling - EXACT SAME AS PLAYERS PAGE
  const handleScroll = useCallback(
    (e: React.UIEvent<HTMLDivElement>) => {
      const { scrollTop, scrollHeight, clientHeight } = e.currentTarget;
      if (
        scrollHeight - scrollTop <= clientHeight * 1.5 &&
        !loadingPlayers &&
        hasMore
      ) {
        fetchPlayers(offset);
      }
    },
    [fetchPlayers, loadingPlayers, hasMore, offset]
  );

  // Filter players based on search and team filter - EXACT SAME AS PLAYERS PAGE
  const filteredPlayers = playerIds
    .map(id => playersById[id])
    .filter(
      (player: Player | undefined): player is Player => !!player && !!player.id
    )
    .filter(player => {
      const matchesSearch = player.name
        .toLowerCase()
        .includes(searchTerm.toLowerCase());
      const matchesTeam = teamFilter === 'all' || player.team === teamFilter;
      return matchesSearch && matchesTeam;
    });
  // Deduplicate by player.id before sorting and rendering
  const dedupedPlayers = Array.from(
    new Map(filteredPlayers.map(p => [p.id, p])).values()
  );
  const sortedPlayers = [...dedupedPlayers].sort((a, b) =>
    a.name.localeCompare(b.name)
  );

  // Add state and handlers for search, team filter, and dropdown (copied from Players page)
  const [drillSuggestions, setDrillSuggestions] = useState<DrillSuggestion[]>(
    []
  );

  // Fetch data with validation
  useEffect(() => {
    console.log('useEffect called, starting fetchData');
    const fetchData = async () => {
      try {
        setLoading(true);
        setError(null);

        // Fetch development plans with validation
        const plansResponse = await fetch('/api/development-plans');
        console.log('Plans response status:', plansResponse.status);
        if (plansResponse.ok) {
          const rawPlansData = await plansResponse.json();
          console.log('Raw plans data:', rawPlansData);

          // Handle the API response structure: direct array of plans
          if (Array.isArray(rawPlansData)) {
            // Transform the API data to match our interface
            const transformedPlans = rawPlansData.map((plan: any) => ({
              id: plan.id || '',
              playerId: plan.playerId || '',
              playerName: plan.playerName || 'Unknown Player',
              coachName: 'Unknown Coach', // Not provided by API
              initialObservation: '', // Not provided by API
              objective: plan.objective || '',
              description: plan.objective || '', // Use objective as description
              status: (plan.status || 'draft') as
                | 'draft'
                | 'active'
                | 'completed'
                | 'archived',
              startDate: plan.startDate || new Date().toISOString(),
              endDate: plan.endDate || null, // API can return null
              goals: plan.goals || [],
              tags: plan.tags || [],
              readiness: (plan.readiness || 'medium') as
                | 'high'
                | 'medium'
                | 'low',
              lastUpdated: plan.updatedAt || new Date().toISOString(),
              createdAt: plan.createdAt || new Date().toISOString(),
              updatedAt: plan.updatedAt || new Date().toISOString(),
              // For UI compatibility, add a title field mapped from objective
              title: plan.objective || '',
            }));

            // Skip validation for now to debug the issue
            const validPlans = transformedPlans.filter(
              (plan: any) =>
                plan &&
                typeof plan === 'object' &&
                typeof plan.id === 'string' &&
                typeof plan.playerId === 'string' &&
                typeof plan.playerName === 'string' &&
                typeof plan.objective === 'string' &&
                plan.id.trim() !== '' &&
                plan.playerId.trim() !== '' &&
                plan.playerName.trim() !== '' &&
                plan.objective.trim() !== ''
            ) as DevelopmentPlan[];

            console.log('Valid plans count:', validPlans.length);
            console.log('First valid plan:', validPlans[0]);

            // Deduplicate plans by id
            const uniquePlans = Array.from(
              new Map(validPlans.map(plan => [plan.id, plan])).values()
            );
            console.log('Unique plans count:', uniquePlans.length);
            setPlans(uniquePlans);
          } else {
            console.error(
              'Invalid API response structure for plans:',
              rawPlansData
            );
            setPlans([]);
          }
        }

        // Fetch teams with validation
        const teamsResponse = await fetch('/api/user/teams');
        if (teamsResponse.ok) {
          const rawTeamsData = await teamsResponse.json();

          // Validate teams data
          const validatedTeams = z
            .array(z.object({ id: z.string(), name: z.string() }))
            .safeParse(rawTeamsData);
          if (!validatedTeams.success) {
            console.error('Invalid teams data:', validatedTeams.error);
            throw new Error('Invalid teams data received');
          }

          // Filter out any invalid teams and deduplicate by id
          const validTeams = validatedTeams.data.filter(
            (team): team is Team =>
              team &&
              typeof team === 'object' &&
              typeof team.id === 'string' &&
              typeof team.name === 'string' &&
              team.id.trim() !== '' &&
              team.name.trim() !== ''
          );

          const uniqueTeams = Array.from(
            new Map(validTeams.map(team => [team.id, team])).values()
          );
          setTeams(uniqueTeams);
        }

        // Skip mock drill suggestions for now to debug the issue
        setDrillSuggestions([]);
      } catch (err: any) {
        console.error('Error in fetchData:', err);
        setError(err.message || 'Failed to fetch data');
        setPlans([]);
        setDrillSuggestions([]);
      } finally {
        console.log('Setting loading to false');
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  // Fetch initial players and teams - EXACT SAME AS PLAYERS PAGE
  useEffect(() => {
    // Fetch teams
    fetch('/api/user/teams')
      .then(res => {
        if (!res.ok) {
          console.log('Teams API returned error status:', res.status);
          setTeams([]);
          return;
        }
        return res.json();
      })
      .then(data => {
        if (!data) return; // Skip if no data (error case)

        let arr: unknown = data;
        if (data && Array.isArray(data)) {
          arr = data;
        }
        const result = z
          .array(z.object({ id: z.string(), name: z.string() }))
          .safeParse(arr);
        if (result.success) {
          // Deduplicate teams by id
          const uniqueTeams = Array.from(
            new Map(result.data.map(team => [team.id, team])).values()
          );
          uniqueTeams.sort((a, b) => a.name.localeCompare(b.name));
          setTeams(uniqueTeams);
        } else {
          console.error('Zod validation error for teams:', result.error);
          setTeams([]);
        }
      })
      .catch(error => {
        console.error('Error fetching teams:', error);
        setTeams([]);
      });

    // Fetch initial players
    setLoadingPlayers(true);
    fetch('/api/dashboard/players?offset=0&limit=10')
      .then(res => {
        if (!res.ok) {
          console.log('Players API returned error status:', res.status);
          return null;
        }
        return res.json();
      })
      .then(data => {
        if (!data) return; // Skip if no data (error case)

        if (data.players) {
          const transformedPlayers = data.players.map((player: any) => ({
            id: player.id,
            name: player.name || 'Unknown Player',
            team: player.team || 'No Team',
            status: player.status || 'active',
          }));

          // Reset the list with normalized state
          const playersMap: Record<string, Player> = {};
          const ids: string[] = [];

          transformedPlayers.forEach((player: Player) => {
            if (!playersMap[player.id]) {
              playersMap[player.id] = player;
              ids.push(player.id);
            }
          });

          setPlayersById(playersMap);
          setPlayerIds(ids);
          setOffset(10);
          setHasMore(data.players.length === 10);
        }
      })
      .catch(error => {
        console.error('Error fetching players:', error);
      })
      .finally(() => {
        setLoadingPlayers(false);
      });
  }, []);

  // Reset pagination when search or filter changes - EXACT SAME AS PLAYERS PAGE
  useEffect(() => {
    setOffset(0);
    setHasMore(true);
    fetchPlayers(0, true);
  }, [searchTerm, teamFilter]);

  // Get readiness color
  const getReadinessColor = (readiness: string) => {
    switch (readiness) {
      case 'high':
        return 'bg-green-500';
      case 'medium':
        return 'bg-yellow-500';
      case 'low':
        return 'bg-red-500';
      default:
        return 'bg-gray-500';
    }
  };

  // Get drill suggestions based on selected players
  const getFilteredDrillSuggestions = () => {
    // In real app, this would use AI to generate suggestions based on player needs
    return drillSuggestions;
  };

  // Helper function for goal status icons
  const getGoalStatusIcon = (status: string) => {
    switch (status) {
      case 'completed':
        return <CheckCircle className="h-4 w-4 text-green-500" />;
      case 'in_progress':
        return <Clock className="h-4 w-4 text-yellow-500" />;
      case 'not_started':
        return <Circle className="h-4 w-4 text-gray-500" />;
      default:
        return <Circle className="h-4 w-4 text-gray-500" />;
    }
  };

  // Example loading/error states:
  if (loading) {
    return (
      <div className="h-screen w-screen bg-[#161616] flex items-center justify-center">
        <div className="flex flex-col items-center justify-center w-full">
          <span className="text-zinc-400 text-lg font-semibold mb-4">
            Loading development plans...
          </span>
          <div className="w-8 h-8 border-2 border-[#d8cc97] border-t-transparent rounded-full animate-spin"></div>
        </div>
      </div>
    );
  }
  if (error) {
    return (
      <div className="min-h-screen p-4 bg-[#161616] flex items-center justify-center">
        <div className="bg-red-900/20 border border-red-500 rounded p-4 text-red-300">
          {error}
        </div>
      </div>
    );
  }

  return (
    <div
      className="flex min-h-screen h-full bg-black text-white"
      style={{ background: 'black' }}
    >
      {/* Header - exact replica with coach info */}
      <header
        className="fixed top-0 left-0 w-full z-50 bg-black h-16 flex items-center px-8 border-b border-[#d8cc97] justify-between"
        style={{ boxShadow: 'none' }}
      >
        <span
          className="text-2xl font-bold tracking-wide text-[#d8cc97]"
          style={{ letterSpacing: '0.04em' }}
        >
          MP Player Development
        </span>
        <div className="flex flex-col items-end">
          <span className="text-base font-semibold text-white leading-tight">
            Coach
          </span>
          <span className="text-xs text-[#d8cc97] leading-tight">
            coach@example.com
          </span>
          <span className="text-xs text-white leading-tight">Coach</span>
        </div>
      </header>
      {/* Sidebar */}
      <Sidebar
        user={{
          name: 'Coach',
          email: 'coach@example.com',
          role: 'Coach',
        }}
      />
      {/* Main Content: three columns, below header, to the right of sidebar */}
      <div
        className="flex-1 flex ml-64 pt-16 bg-black min-h-screen"
        style={{ background: 'black', minHeight: '100vh' }}
      >
        {/* LEFT PANE: Player List - EXACT SAME AS PLAYERS PAGE */}
        <div
          className="w-1/4 border-r border-zinc-800 p-6 bg-black flex flex-col justify-start min-h-screen"
          style={{ background: 'black' }}
        >
          <div className="flex justify-between items-center mb-6">
            <h2 className="text-xl font-bold text-[#d8cc97] mt-0">Players</h2>
            <UniversalButton.Primary
              size="sm"
              onClick={() => {}}
              leftIcon={<Plus size={16} />}
            >
              Add Player
            </UniversalButton.Primary>
          </div>
          {/* Search Input */}
          <div className="relative mb-6">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-zinc-400 w-4 h-4" />
            <input
              type="text"
              placeholder="Search players..."
              value={searchTerm}
              onChange={e => setSearchTerm(e.target.value)}
              className="w-full pl-10 pr-4 py-3 rounded bg-zinc-800 text-sm placeholder-gray-400 border border-zinc-700 focus:outline-none focus:border-[#d8cc97]"
            />
          </div>
          {/* Team Filter */}
          <div className="relative mb-6">
            <Filter className="absolute left-3 top-1/2 transform -translate-y-1/2 text-zinc-400 w-4 h-4" />
            <div className="relative">
              <button
                onClick={() => setIsTeamDropdownOpen(!isTeamDropdownOpen)}
                className="w-full pl-10 pr-4 py-3 rounded bg-zinc-800 text-sm text-white border border-zinc-700 focus:outline-none focus:border-[#d8cc97] flex items-center justify-between"
              >
                <span>{teamFilter === 'all' ? 'All Teams' : teamFilter}</span>
                <span className="text-zinc-400">â–¼</span>
              </button>
              {isTeamDropdownOpen && (
                <div className="absolute top-full left-0 right-0 mt-1 bg-zinc-800 border border-zinc-700 rounded shadow-lg z-10 max-h-48 overflow-y-auto">
                  <button
                    onClick={() => {
                      setTeamFilter('all');
                      setIsTeamDropdownOpen(false);
                    }}
                    className="w-full px-4 py-2 text-left text-sm text-white hover:bg-zinc-700"
                  >
                    All Teams
                  </button>
                  {teams.map(team => (
                    <button
                      key={team.id}
                      onClick={() => {
                        setTeamFilter(team.name);
                        setIsTeamDropdownOpen(false);
                      }}
                      className="w-full px-4 py-2 text-left text-sm text-white hover:bg-zinc-700"
                    >
                      {team.name}
                    </button>
                  ))}
                </div>
              )}
            </div>
          </div>
          {/* Player List - Fixed height for exactly 10 player cards */}
          <div
            className="flex-1 overflow-y-auto space-y-2"
            style={{ maxHeight: '400px' }} // Exactly 10 player cards (10 * 40px)
            onScroll={handleScroll}
          >
            {sortedPlayers.length === 0 ? (
              <div className="text-center py-8">
                <Shield className="text-zinc-700 w-12 h-12 mx-auto mb-4" />
                <p className="text-zinc-400 text-sm">No players found</p>
              </div>
            ) : (
              sortedPlayers.map((player: Player) => (
                <div
                  key={player.id}
                  onClick={() => handlePlayerSelect(player)}
                  className={`p-3 rounded-lg cursor-pointer transition-all ${
                    selectedPlayer?.id === player.id
                      ? 'bg-[#d8cc97]/20 border border-[#d8cc97]'
                      : 'bg-zinc-800/50 border border-zinc-700 hover:bg-zinc-800 hover:border-zinc-600'
                  }`}
                >
                  <div className="flex items-center justify-between">
                    <div>
                      <p className="font-medium text-white">
                        {String(player.name)}
                      </p>
                      <p className="text-sm text-zinc-400">{player.team}</p>
                    </div>
                    <div
                      className={`w-2 h-2 rounded-full ${
                        player.status === 'active'
                          ? 'bg-green-500'
                          : 'bg-zinc-500'
                      }`}
                    />
                  </div>
                </div>
              ))
            )}
          </div>
        </div>
        {/* CENTER and RIGHT columns: leave unchanged except for being direct siblings in the flex layout */}
        <div className="flex-1 min-w-0">
          {/* CENTER: Active PDP Summary View */}
          <div className="space-y-6">
            <div className="flex justify-between items-center">
              <h2 className="text-xl font-bold text-[#d8cc97]">
                Active PDP Summary
              </h2>
              <div className="flex gap-2">
                <UniversalButton.Secondary size="sm">
                  <Edit className="h-3 w-3 mr-1" />
                  Edit Plans
                </UniversalButton.Secondary>
                <UniversalButton.Primary size="sm">
                  Export Plan
                </UniversalButton.Primary>
              </div>
            </div>
            {plans.length === 0 ? (
              <div className="bg-zinc-900 border border-zinc-800 rounded-lg p-6 flex flex-col items-center justify-center h-96">
                <Target className="text-zinc-700 w-20 h-20 mb-5" />
                <h3 className="text-lg font-medium text-white mb-2">
                  No Development Plans Found
                </h3>
                <p className="text-sm text-zinc-400 max-w-md mb-6 text-center">
                  No development plans are currently available for the players.
                </p>
              </div>
            ) : (
              <div className="space-y-4">
                {plans.length > 0 ? (
                  plans.map(plan => (
                    <div
                      key={plan.id}
                      className="bg-zinc-900 border border-zinc-800 rounded-lg p-6"
                    >
                      <div className="flex justify-between items-start mb-4">
                        <div>
                          <h3 className="text-lg font-bold text-[#d8cc97]">
                            {plan.title ? plan.title : 'Untitled Plan'}
                          </h3>
                          <p className="text-sm text-zinc-300 mb-2">
                            {plan.objective
                              ? plan.objective
                              : 'No objective provided'}
                          </p>
                          <p className="text-sm text-zinc-400">
                            Player: {plan.playerName}
                          </p>
                        </div>
                        <div className="flex items-center space-x-2">
                          <span
                            className={`inline-flex items-center px-2 py-1 rounded-full text-xs ${
                              plan.status === 'active'
                                ? 'bg-green-500/20 text-green-500'
                                : plan.status === 'completed'
                                  ? 'bg-blue-500/20 text-blue-500'
                                  : 'bg-yellow-500/20 text-yellow-500'
                            }`}
                          >
                            {(plan.status || 'draft').charAt(0).toUpperCase() +
                              (plan.status || 'draft').slice(1)}
                          </span>
                          <div
                            className={`w-2 h-2 rounded-full ${getReadinessColor(plan.readiness || 'medium')}`}
                          />
                        </div>
                      </div>
                      <div className="space-y-4">
                        <div>
                          <h4 className="text-sm font-medium text-zinc-400 mb-2">
                            Focus Areas
                          </h4>
                          <div className="flex flex-wrap gap-2">
                            {(plan.tags || []).map((tag, index) => (
                              <span
                                key={index}
                                className="inline-flex items-center px-2 py-1 rounded-full text-xs bg-zinc-800 text-zinc-300"
                              >
                                {tag}
                              </span>
                            ))}
                          </div>
                        </div>
                        <div>
                          <h4 className="text-sm font-medium text-zinc-400 mb-2">
                            Active Goals
                          </h4>
                          <div className="space-y-2">
                            {(plan.goals || [])
                              .filter(g => g.status !== 'completed')
                              .slice(0, 3)
                              .map(goal => (
                                <div
                                  key={goal.id}
                                  className="flex items-center space-x-2 p-2 bg-zinc-800/50 rounded"
                                >
                                  {getGoalStatusIcon(goal.status)}
                                  <span className="text-sm text-white">
                                    {goal.title}
                                  </span>
                                </div>
                              ))}
                          </div>
                        </div>
                      </div>
                    </div>
                  ))
                ) : (
                  <div className="bg-zinc-900 border border-zinc-800 rounded-lg p-6 text-center">
                    <Target className="text-zinc-700 w-16 h-16 mx-auto mb-4" />
                    <p className="text-zinc-400 mb-2">
                      No development plans found for selected players
                    </p>
                    <p className="text-sm text-zinc-500">
                      Create development plans to track player progress
                    </p>
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
        <div style={{ width: 340 }}>
          {/* RIGHT: Drill Suggestions */}
          <h2 className="text-xl font-bold mb-6 text-[#d8cc97]">
            Drill Suggestions
          </h2>
          {plans.length === 0 ? (
            <div className="text-center py-8">
              <Lightbulb className="text-zinc-700 w-12 h-12 mx-auto mb-4" />
              <p className="text-zinc-400 text-sm">
                No development plans available
              </p>
              <p className="text-xs text-zinc-500 mt-1">
                Drill suggestions will appear when plans are available
              </p>
            </div>
          ) : (
            <div className="space-y-4">
              {/* Smart Logic Header */}
              <div className="bg-zinc-800/50 border border-zinc-700 rounded-lg p-4">
                <div className="flex items-center space-x-2 mb-2">
                  <Zap className="text-[#d8cc97] w-4 h-4" />
                  <h4 className="font-medium text-white">Smart Suggestions</h4>
                </div>
                <p className="text-xs text-zinc-400">
                  Showing drill suggestions based on available development plans
                </p>
              </div>
              {/* Drill Suggestions */}
              <div className="space-y-3">
                {getFilteredDrillSuggestions().map(drill => (
                  <div
                    key={drill.id}
                    className="bg-zinc-800/50 border border-zinc-700 rounded-lg p-4"
                  >
                    <div className="flex justify-between items-start mb-2">
                      <h4 className="font-medium text-white text-sm">
                        {drill.name}
                      </h4>
                      <span
                        className={`text-xs px-2 py-1 rounded ${
                          drill.difficulty === 'beginner'
                            ? 'bg-green-500/20 text-green-500'
                            : drill.difficulty === 'intermediate'
                              ? 'bg-yellow-500/20 text-yellow-500'
                              : 'bg-red-500/20 text-red-500'
                        }`}
                      >
                        {drill.difficulty}
                      </span>
                    </div>
                    <p className="text-xs text-zinc-400 mb-2">
                      {drill.description}
                    </p>
                    <p className="text-xs text-zinc-500">
                      Duration: {drill.duration} minutes
                    </p>
                    <p className="text-xs text-zinc-500">
                      Players needed: {drill.players}
                    </p>
                    <div className="flex flex-wrap gap-1 mt-2">
                      {drill.cues.map((cue, index) => (
                        <span
                          key={index}
                          className="inline-flex items-center px-2 py-1 rounded-full text-xs bg-zinc-700 text-zinc-300"
                        >
                          {cue}
                        </span>
                      ))}
                    </div>
                    <div className="flex flex-wrap gap-1 mt-2">
                      {drill.constraints.map((constraint, index) => (
                        <span
                          key={index}
                          className="inline-flex items-center px-2 py-1 rounded-full text-xs bg-zinc-700 text-zinc-300"
                        >
                          {constraint}
                        </span>
                      ))}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import {
  Edit,
  ChevronDown,
  ChevronUp,
  Search,
  Filter,
  Target,
} from 'lucide-react';
import { Sidebar } from '@/components/ui/Sidebar';
import { z } from 'zod';

// Zod schemas for validation
const DrillSchema = z.object({
  id: z.string(),
  name: z.string(),
  category: z.enum([
    'shooting',
    'ball_handling',
    'defense',
    'conditioning',
    'team_play',
    'passing',
    'rebounding',
  ]),
  difficulty: z.enum(['beginner', 'intermediate', 'advanced']),
  duration: z.number(),
  description: z.string(),
  instructions: z.array(z.string()),
  cues: z.array(z.string()),
  constraints: z.array(z.string()),
  players: z.number(),
  equipment: z.array(z.string()),
  variations: z.array(z.string()),
  createdAt: z.string(),
  updatedAt: z.string(),
});
const DrillsArraySchema = z.array(DrillSchema);

const PlayerSchema = z.object({
  id: z.string(),
  name: z.string(),
  team: z.string(),
  status: z.string(),
});
const PlayersArraySchema = z.array(PlayerSchema);

const TeamSchema = z.object({
  id: z.string(),
  name: z.string(),
});
const TeamsArraySchema = z.array(TeamSchema);

// Types for drills
interface Drill {
  id: string;
  name: string;
  category:
    | 'shooting'
    | 'ball_handling'
    | 'defense'
    | 'conditioning'
    | 'team_play'
    | 'passing'
    | 'rebounding';
  difficulty: 'beginner' | 'intermediate' | 'advanced';
  duration: number; // minutes
  description: string;
  instructions: string[];
  cues: string[];
  constraints: string[];
  players: number; // min players needed
  equipment: string[];
  variations: string[];
  createdAt: string;
  updatedAt: string;
}

interface Player {
  id: string;
  name: string;
  team: string;
  status: string;
}

interface Team {
  id: string;
  name: string;
}

// Main component
export default function DrillsPage() {
  const [drills, setDrills] = useState<Drill[]>([]);
  const [selectedDrill, setSelectedDrill] = useState<Drill | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  // Pagination state for drills
  const [page, setPage] = useState(1);
  const pageSize = 5;

  // Player/team data for left column
  const [players, setPlayers] = useState<Player[]>([]);
  const [teams, setTeams] = useState<Team[]>([]);
  const [selectedDrillId, setSelectedDrillId] = useState<string | null>(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [categoryFilter, setCategoryFilter] = useState('all');
  const [difficultyFilter, setDifficultyFilter] = useState('all');
  const [isCategoryDropdownOpen, setIsCategoryDropdownOpen] = useState(false);
  const [isDifficultyDropdownOpen, setIsDifficultyDropdownOpen] =
    useState(false);

  // Filter drills by selected category
  const filteredDrills = selectedDrillId
    ? drills.filter(drill => drill.id === selectedDrillId)
    : drills;

  const filteredDrillsList = drills.filter(drill => {
    const matchesSearch = drill.name
      .toLowerCase()
      .includes(searchTerm.toLowerCase());
    const matchesCategory =
      categoryFilter === 'all' || drill.category === categoryFilter;
    const matchesDifficulty =
      difficultyFilter === 'all' || drill.difficulty === difficultyFilter;
    return matchesSearch && matchesCategory && matchesDifficulty;
  });

  // Handle drill selection with toggle functionality
  const handleDrillSelect = (drillId: string) => {
    if (selectedDrillId === drillId) {
      // Clicking the same drill again - show all drills
      setSelectedDrillId(null);
    } else {
      // Clicking a different drill - filter to their details
      setSelectedDrillId(drillId);
    }
  };

  // Fetch real data with validation
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        setError(null);

        // Mock drills data with validation - in real app, this would come from API
        const mockDrills: Drill[] = [
          {
            id: '1',
            name: '3-Point Shooting Progression',
            category: 'shooting',
            difficulty: 'intermediate',
            duration: 30,
            description:
              'Progressive shooting drill focusing on form and accuracy',
            instructions: [
              'Start at 5 feet from basket',
              'Make 5 shots before moving back',
              'Move back 2 feet after each set',
              'Continue until reaching 3-point line',
            ],
            cues: ['Follow through', 'Square shoulders', 'Bend knees'],
            constraints: ['Must make 5 in a row', 'No dribbling'],
            players: 2,
            equipment: ['Basketball', 'Basket'],
            variations: ['Add defender', 'Time pressure'],
            createdAt: '2024-01-01',
            updatedAt: '2024-01-15',
          },
          {
            id: '2',
            name: 'Ball Handling Circuit',
            category: 'ball_handling',
            difficulty: 'beginner',
            duration: 20,
            description: 'Station-based ball handling improvement',
            instructions: [
              'Set up 4 cones in a square',
              'Dribble around each cone',
              'Switch hands at each cone',
              'Complete 3 full circuits',
            ],
            cues: ['Keep head up', 'Finger tips', 'Low stance'],
            constraints: ['No looking down', 'Speed variations'],
            players: 1,
            equipment: ['Basketball', 'Cones'],
            variations: ['Add obstacles', 'Speed drills'],
            createdAt: '2024-01-01',
            updatedAt: '2024-01-15',
          },
          {
            id: '3',
            name: 'Defensive Slides',
            category: 'defense',
            difficulty: 'intermediate',
            duration: 15,
            description: 'Defensive footwork and positioning',
            instructions: [
              'Start in defensive stance',
              'Slide laterally without crossing feet',
              'Keep hands active and ready',
              'Maintain low center of gravity',
            ],
            cues: ['Stay low', 'Active hands', "Slide don't cross"],
            constraints: ['Maintain stance', 'No crossing feet'],
            players: 2,
            equipment: ['Cones'],
            variations: ['Add offensive player', 'Speed variations'],
            createdAt: '2024-01-01',
            updatedAt: '2024-01-15',
          },
        ];

        // Validate drills data
        const validatedDrills = DrillsArraySchema.safeParse(mockDrills);
        if (!validatedDrills.success) {
          console.error('Invalid drills data:', validatedDrills.error);
          throw new Error('Invalid drills data received');
        }

        // Filter out any invalid drills
        const validDrills = validatedDrills.data.filter(
          (drill): drill is Drill =>
            drill &&
            typeof drill === 'object' &&
            typeof drill.id === 'string' &&
            typeof drill.name === 'string' &&
            typeof drill.description === 'string' &&
            Array.isArray(drill.instructions) &&
            Array.isArray(drill.cues) &&
            Array.isArray(drill.constraints) &&
            Array.isArray(drill.equipment) &&
            Array.isArray(drill.variations) &&
            typeof drill.createdAt === 'string' &&
            typeof drill.updatedAt === 'string' &&
            drill.id.trim() !== '' &&
            drill.name.trim() !== '' &&
            drill.description.trim() !== '' &&
            drill.createdAt.trim() !== '' &&
            drill.updatedAt.trim() !== ''
        );

        // Deduplicate drills by id
        const uniqueDrills = Array.from(
          new Map(validDrills.map(drill => [drill.id, drill])).values()
        );
        setDrills(uniqueDrills);

        // Fetch players with validation
        const playersResponse = await fetch(
          '/api/dashboard/players?offset=0&limit=10'
        );
        if (playersResponse.ok) {
          const rawPlayersData = await playersResponse.json();
          // Handle the API response structure: { players: [...], total: number }
          if (
            rawPlayersData &&
            rawPlayersData.players &&
            Array.isArray(rawPlayersData.players)
          ) {
            const transformedRawPlayers = rawPlayersData.players.map(
              (player: any) => ({
                id: player.id,
                name: player.name || 'Unknown Player',
                team: player.team || 'No Team',
                status: player.status || 'active',
              })
            );

            // Validate players data
            const validatedPlayers = PlayersArraySchema.safeParse(
              transformedRawPlayers
            );
            if (!validatedPlayers.success) {
              console.error('Invalid players data:', validatedPlayers.error);
              throw new Error('Invalid players data received');
            }

            // Filter out any invalid players
            const validPlayers = validatedPlayers.data.filter(
              (player): player is Player =>
                player &&
                typeof player === 'object' &&
                typeof player.id === 'string' &&
                typeof player.name === 'string' &&
                typeof player.team === 'string' &&
                player.id.trim() !== '' &&
                player.name.trim() !== '' &&
                player.team.trim() !== ''
            );

            // Deduplicate players by id
            const uniquePlayers = Array.from(
              new Map(validPlayers.map(player => [player.id, player])).values()
            );
            setPlayers(uniquePlayers);
          } else {
            console.error(
              'Invalid API response structure for players:',
              rawPlayersData
            );
            setPlayers([]);
          }
        }

        // Fetch teams with validation
        const teamsResponse = await fetch('/api/user/teams');
        if (teamsResponse.ok) {
          const rawTeamsData = await teamsResponse.json();

          // Validate teams data
          const validatedTeams = TeamsArraySchema.safeParse(rawTeamsData);
          if (!validatedTeams.success) {
            console.error('Invalid teams data:', validatedTeams.error);
            throw new Error('Invalid teams data received');
          }

          // Filter out any invalid teams and deduplicate by id
          const validTeams = validatedTeams.data.filter(
            (team): team is Team =>
              team &&
              typeof team === 'object' &&
              typeof team.id === 'string' &&
              typeof team.name === 'string' &&
              team.id.trim() !== '' &&
              team.name.trim() !== ''
          );

          const uniqueTeams = Array.from(
            new Map(validTeams.map(team => [team.id, team])).values()
          );
          setTeams(uniqueTeams);
        }

        if (uniqueDrills.length > 0 && uniqueDrills[0]) {
          setSelectedDrill(uniqueDrills[0]);
        }
      } catch (err) {
        console.error('Error fetching data:', err);
        setError(err instanceof Error ? err.message : 'Failed to fetch data');
        setDrills([]);
        setPlayers([]);
        setTeams([]);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // Reset page when drill selection changes
  useEffect(() => {
    setPage(1);
  }, [selectedDrillId]);

  if (loading) {
    return (
      <div className="h-screen w-screen bg-[#161616] flex items-center justify-center">
        <div className="flex flex-col items-center justify-center w-full">
          <span className="text-zinc-400 text-lg font-semibold mb-4">
            Loading drills...
          </span>
          <div className="w-8 h-8 border-2 border-[#d8cc97] border-t-transparent rounded-full animate-spin"></div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen p-4 bg-[#161616] flex items-center justify-center">
        <div className="bg-red-900/20 border border-red-500 rounded p-4 text-red-300">
          {error}
        </div>
      </div>
    );
  }

  return (
    <div
      className="flex min-h-screen h-full bg-black text-white"
      style={{ background: 'black' }}
    >
      {/* Header - exact replica with coach info */}
      <header
        className="fixed top-0 left-0 w-full z-50 bg-black h-16 flex items-center px-8 border-b border-[#d8cc97] justify-between"
        style={{ boxShadow: 'none' }}
      >
        <span
          className="text-2xl font-bold tracking-wide text-[#d8cc97]"
          style={{ letterSpacing: '0.04em' }}
        >
          MP Player Development
        </span>
        <div className="flex flex-col items-end">
          <span className="text-base font-semibold text-white leading-tight">
            Coach
          </span>
          <span className="text-xs text-[#d8cc97] leading-tight">
            coach@example.com
          </span>
          <span className="text-xs text-white leading-tight">Coach</span>
        </div>
      </header>
      {/* Sidebar */}
      <Sidebar
        user={{
          name: 'Coach',
          email: 'coach@example.com',
          role: 'Coach',
        }}
      />
      {/* Main Content */}
      <div
        className="flex-1 flex ml-64 pt-16 bg-black min-h-screen"
        style={{ background: 'black', minHeight: '100vh' }}
      >
        {/* LEFT PANE: Drill Categories */}
        <div
          className="w-1/4 border-r border-zinc-800 p-6 bg-black flex flex-col justify-start min-h-screen"
          style={{ background: 'black' }}
        >
          <h2 className="text-xl font-bold mb-6 text-[#d8cc97] mt-0">Drills</h2>

          {/* Search Input */}
          <div className="relative mb-6">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-zinc-400 w-4 h-4" />
            <input
              type="text"
              placeholder="Search..."
              value={searchTerm}
              onChange={e => setSearchTerm(e.target.value)}
              className="w-full pl-10 pr-4 py-3 rounded bg-zinc-800 text-sm placeholder-gray-400 border border-zinc-700 focus:outline-none focus:border-[#d8cc97]"
            />
          </div>

          {/* Category Filter */}
          <div className="relative mb-6">
            <Filter className="absolute left-3 top-1/2 transform -translate-y-1/2 text-zinc-400 w-4 h-4" />
            <button
              onClick={() => setIsCategoryDropdownOpen(!isCategoryDropdownOpen)}
              className="w-full pl-10 pr-4 py-3 text-left bg-zinc-800 border border-zinc-700 rounded text-sm text-white focus:outline-none focus:border-[#d8cc97] flex items-center justify-between"
            >
              <span>
                {categoryFilter === 'all' ? 'All Categories' : categoryFilter}
              </span>
              {isCategoryDropdownOpen ? (
                <ChevronUp size={16} />
              ) : (
                <ChevronDown size={16} />
              )}
            </button>
            {isCategoryDropdownOpen && (
              <div className="absolute z-10 mt-1 w-full bg-zinc-800 border border-zinc-700 rounded shadow-lg overflow-hidden">
                <button
                  onClick={() => {
                    setCategoryFilter('all');
                    setIsCategoryDropdownOpen(false);
                  }}
                  className="w-full text-left px-4 py-2 text-sm hover:bg-zinc-700 text-white"
                >
                  All Categories
                </button>
                {[
                  'shooting',
                  'ball_handling',
                  'defense',
                  'conditioning',
                  'team_play',
                  'passing',
                  'rebounding',
                ].map(category => (
                  <button
                    key={category}
                    onClick={() => {
                      setCategoryFilter(category);
                      setIsCategoryDropdownOpen(false);
                    }}
                    className="w-full text-left px-4 py-2 text-sm hover:bg-zinc-700 text-white capitalize"
                  >
                    {category.replace('_', ' ')}
                  </button>
                ))}
              </div>
            )}
          </div>

          {/* Difficulty Filter */}
          <div className="relative mb-6">
            <Target className="absolute left-3 top-1/2 transform -translate-y-1/2 text-zinc-400 w-4 h-4" />
            <button
              onClick={() =>
                setIsDifficultyDropdownOpen(!isDifficultyDropdownOpen)
              }
              className="w-full pl-10 pr-4 py-3 text-left bg-zinc-800 border border-zinc-700 rounded text-sm text-white focus:outline-none focus:border-[#d8cc97] flex items-center justify-between"
            >
              <span>
                {difficultyFilter === 'all'
                  ? 'All Difficulties'
                  : difficultyFilter}
              </span>
              {isDifficultyDropdownOpen ? (
                <ChevronUp size={16} />
              ) : (
                <ChevronDown size={16} />
              )}
            </button>
            {isDifficultyDropdownOpen && (
              <div className="absolute z-10 mt-1 w-full bg-zinc-800 border border-zinc-700 rounded shadow-lg overflow-hidden">
                <button
                  onClick={() => {
                    setDifficultyFilter('all');
                    setIsDifficultyDropdownOpen(false);
                  }}
                  className="w-full text-left px-4 py-2 text-sm hover:bg-zinc-700 text-white"
                >
                  All Difficulties
                </button>
                {['beginner', 'intermediate', 'advanced'].map(difficulty => (
                  <button
                    key={difficulty}
                    onClick={() => {
                      setDifficultyFilter(difficulty);
                      setIsDifficultyDropdownOpen(false);
                    }}
                    className="w-full text-left px-4 py-2 text-sm hover:bg-zinc-700 text-white capitalize"
                  >
                    {difficulty}
                  </button>
                ))}
              </div>
            )}
          </div>

          {/* Drill List */}
          <div className="space-y-3 max-h-[calc(100vh-400px)] overflow-y-auto">
            {filteredDrillsList.length === 0 ? (
              <div className="text-sm text-gray-500 text-center py-4">
                No drills found
              </div>
            ) : (
              filteredDrillsList.map(drill => (
                <div
                  key={drill.id}
                  onClick={() => handleDrillSelect(drill.id)}
                  className={`p-4 rounded cursor-pointer transition-all ${
                    selectedDrillId === drill.id
                      ? 'bg-[#d8cc97] text-black font-semibold'
                      : 'bg-zinc-800 hover:bg-zinc-700'
                  }`}
                >
                  <p className="font-medium">{drill.name}</p>
                  <p className="text-xs text-gray-400">
                    {drill.category.replace('_', ' ')} â€¢ {drill.difficulty} â€¢{' '}
                    {drill.duration}min
                  </p>
                </div>
              ))
            )}
          </div>
        </div>

        {/* CENTER PANE: Drill Details */}
        <div
          className="w-1/2 border-r border-zinc-800 p-8 bg-black flex flex-col justify-start min-h-screen"
          style={{ background: 'black' }}
        >
          <h2 className="text-xl font-bold mb-6 text-[#d8cc97] mt-0">
            {selectedDrillId
              ? `${drills.find(d => d.id === selectedDrillId)?.name}`
              : 'All Drills'}
          </h2>

          {selectedDrill ? (
            <div className="space-y-6">
              {/* Drill Details Card */}
              <div
                className="bg-zinc-800 p-6 rounded"
                style={{ background: '#181818' }}
              >
