        {/* RIGHT PANE: Drill Management */}
        <div
          className="w-1/4 p-8 bg-black flex flex-col justify-start min-h-screen"
          style={{ background: 'black' }}
        >
          <h2 className="text-xl font-bold mb-6 text-[#d8cc97] mt-0">
            Management
          </h2>

          {/* Quick Drill Creation */}
          <div className="bg-zinc-800 p-6 rounded mb-6">
            <h3 className="text-base font-bold text-[#d8cc97] mb-4">
              Quick Actions
            </h3>
            <div className="space-y-3">
              <button className="w-full p-3 bg-[#d8cc97] text-black rounded text-sm font-semibold hover:bg-[#b3a14e] transition-colors">
                Create New Drill
              </button>
              <button className="w-full p-3 bg-zinc-700 text-white rounded text-sm font-semibold hover:bg-zinc-600 transition-colors">
                Import Drill Library
              </button>
              <button className="w-full p-3 bg-zinc-700 text-white rounded text-sm font-semibold hover:bg-zinc-600 transition-colors">
                Share Drill
              </button>
            </div>
          </div>

          {/* Drill Favorites */}
          <div className="bg-zinc-800 p-6 rounded mb-6">
            <h3 className="text-base font-bold text-[#d8cc97] mb-4">
              Favorites
            </h3>
            <div className="space-y-3">
              <div className="p-3 bg-zinc-700 rounded cursor-pointer hover:bg-zinc-600 transition-colors">
                <p className="text-sm font-semibold text-white">
                  3-Point Shooting Progression
                </p>
                <p className="text-xs text-zinc-400">20 min â€¢ Intermediate</p>
              </div>
              <div className="p-3 bg-zinc-700 rounded cursor-pointer hover:bg-zinc-600 transition-colors">
                <p className="text-sm font-semibold text-white">
                  Ball Handling Circuit
                </p>
                <p className="text-xs text-zinc-400">15 min â€¢ Beginner</p>
              </div>
            </div>
          </div>

          {/* Related Drills */}
          <div className="bg-zinc-800 p-6 rounded">
            <h3 className="text-base font-bold text-[#d8cc97] mb-4">
              Related Drills
            </h3>
            <div className="space-y-3">
              {drills
                .filter(
                  d =>
                    d.category === selectedDrill?.category &&
                    d.id !== selectedDrill?.id
                )
                .slice(0, 3)
                .map(drill => (
                  <div
                    key={drill.id}
                    className="p-3 bg-zinc-700 rounded cursor-pointer hover:bg-zinc-600 transition-colors"
                  >
                    <p className="text-sm font-semibold text-white">
                      {drill.name}
                    </p>
                    <p className="text-xs text-zinc-400">
                      {drill.duration} min â€¢ {drill.difficulty}
                    </p>
                  </div>
                ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

'use client';

import React, { useState, useEffect, useRef, useCallback } from 'react';
import {
  ChevronDown,
  ChevronUp,
  Search,
  Filter,
  Shield,
  Plus,
} from 'lucide-react';
import { Sidebar } from '@/components/ui/Sidebar';
import { Calendar } from '@/components/ui/calendar';
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from '@/components/ui/popover';
import { Button } from '@/components/ui/button';
import type { DateRange } from 'react-day-picker';
import type {
  Player as SharedPlayer,
} from '@/components/basketball/PlayerListCard';
import { z } from 'zod';

// Types for observations (matching actual API response)
interface Observation {
  id: string;
  playerId: string;
  playerFirstName?: string;
  playerLastName?: string;
  playerName: string;
  title: string;
  description: string;
  rating: number;
  date: string;
  tags: string[];
  createdAt: string;
  updatedAt: string | null;
}

interface Team {
  id: string;
  name: string;
}

// Main component
export default function ObservationsPage() {
  const [observations, setObservations] = useState<Observation[]>([]);
  const [totalObservations, setTotalObservations] = useState<number>(0);
  const [obsOffset, setObsOffset] = useState(0);
  const [obsLoadingMore, setObsLoadingMore] = useState(false);
  const obsLimit = 20;
  const obsListRef = useRef<HTMLDivElement>(null);
  const [selectedObservation, setSelectedObservation] =
    useState<Observation | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  // Pagination state for observations
  const [pageSize, setPageSize] = useState<number | 'all'>(25);
  const [page, setPage] = useState(1);
  const [dateRange, setDateRange] = useState<DateRange | undefined>(undefined);
  const [showDatePicker, setShowDatePicker] = useState(false);

  // Player/team data for left column - EXACT SAME AS PLAYERS PAGE
  const [playersById, setPlayersById] = useState<Record<string, SharedPlayer>>({});
  const [playerIds, setPlayerIds] = useState<string[]>([]);
  const [teams, setTeams] = useState<Team[]>([]);
  const [selectedPlayer, setSelectedPlayer] = useState<SharedPlayer | null>(null);

  // Infinite scroll state - EXACT SAME AS PLAYERS PAGE
  const [loadingPlayers, setLoadingPlayers] = useState(false);
  const [hasMore, setHasMore] = useState(true);
  const [offset, setOffset] = useState(0);

  // Search and filter state - EXACT SAME AS PLAYERS PAGE
  const [searchTerm, setSearchTerm] = useState('');
  const [teamFilter, setTeamFilter] = useState('all');
  const [isTeamDropdownOpen, setIsTeamDropdownOpen] = useState(false);

  // Filter observations by selected player
  const filteredObservations =
    selectedPlayer
      ? observations.filter(obs => obs.playerId === selectedPlayer.id)
      : observations;

  // Date range filter logic
  const filteredByDate = observations.filter(obs => {
    if (!dateRange?.from || !dateRange?.to) return true;
    const obsDate = new Date(obs.date);
    return obsDate >= dateRange.from && obsDate <= dateRange.to;
  });

  // Pagination logic
  const paginatedObservations =
    pageSize === 'all'
      ? filteredByDate
      : filteredByDate.slice((page - 1) * pageSize, page * pageSize);
  const totalPages =
    pageSize === 'all'
      ? 1
      : Math.ceil(filteredByDate.length / (pageSize as number));

  // Handler for selecting a player - EXACT SAME AS PLAYERS PAGE
  const handlePlayerSelect = (player: SharedPlayer) => {
    setSelectedPlayer(player);
  };

  // Fetch players with pagination - EXACT SAME AS PLAYERS PAGE
  const fetchPlayers = useCallback(
    async (currentOffset: number = 0, reset: boolean = false) => {
      setLoadingPlayers(true);
      try {
        const response = await fetch(
          `/api/dashboard/players?offset=${currentOffset}&limit=10`
        );
        const data = await response.json();

        if (data.players) {
          const transformedPlayers = data.players.map((player: any) => ({
            id: player.id,
            name: player.name || 'Unknown Player',
            team: player.team || 'No Team',
            status: player.status || 'active',
          }));

          if (reset) {
            // Reset the list with normalized state
            const playersMap: Record<string, SharedPlayer> = {};
            const ids: string[] = [];

            transformedPlayers.forEach((player: SharedPlayer) => {
              if (!playersMap[player.id]) {
                playersMap[player.id] = player;
                ids.push(player.id);
              }
            });

            setPlayersById(playersMap);
            setPlayerIds(ids);
            setOffset(10);
          } else {
            // Append to existing list with normalized state
            setPlayersById(prevPlayersById => {
              const newPlayersById = { ...prevPlayersById };
              const newIds: string[] = [];

              transformedPlayers.forEach((player: SharedPlayer) => {
                if (!newPlayersById[player.id]) {
                  newPlayersById[player.id] = player;
                  newIds.push(player.id);
                }
              });

              setPlayerIds(prevIds => [...prevIds, ...newIds]);
              return newPlayersById;
            });
            setOffset(prev => prev + 10);
          }

          setHasMore(data.players.length === 10);
        }
      } catch (error) {
        console.error('Error fetching players:', error);
      } finally {
        setLoadingPlayers(false);
      }
    },
    []
  );

  // Load more players when scrolling - EXACT SAME AS PLAYERS PAGE
  const handleScroll = useCallback(
    (e: React.UIEvent<HTMLDivElement>) => {
      const { scrollTop, scrollHeight, clientHeight } = e.currentTarget;
      if (
        scrollHeight - scrollTop <= clientHeight * 1.5 &&
        !loadingPlayers &&
        hasMore
      ) {
        fetchPlayers(offset);
      }
    },
    [fetchPlayers, loadingPlayers, hasMore, offset]
  );

  // Filter players based on search and team filter - EXACT SAME AS PLAYERS PAGE
  const filteredPlayers = playerIds
    .map(id => playersById[id])
    .filter(
      (player: SharedPlayer | undefined): player is SharedPlayer =>
        !!player && !!player.id
    )
    .filter(player => {
      const matchesSearch = player.name
        .toLowerCase()
        .includes(searchTerm.toLowerCase());
      const matchesTeam = teamFilter === 'all' || player.team === teamFilter;
      return matchesSearch && matchesTeam;
    });
  // Deduplicate by player.id before sorting and rendering
  const dedupedPlayers = Array.from(
    new Map(filteredPlayers.map(p => [p.id, p])).values()
  );
  const sortedPlayers = [...dedupedPlayers].sort((a, b) =>
    a.name.localeCompare(b.name)
  );

  // Load more observations when scrolling
  const handleObsScrollLoad = (e: React.UIEvent<HTMLDivElement>) => {
    const { scrollTop, scrollHeight, clientHeight } = e.currentTarget;
    if (
      scrollHeight - scrollTop <= clientHeight * 1.5 &&
      !obsLoadingMore &&
      observations.length < totalObservations
    ) {
      handleObsScroll();
    }
  };

  // Fetch real data
  useEffect(() => {
    const fetchInitialData = async () => {
      try {
        setLoading(true);
        setError(null);

        // Fetch initial observations with pagination
        const observationsResponse = await fetch(
          `/api/observations?offset=0&limit=${obsLimit}`
        );
        if (!observationsResponse.ok) {
          throw new Error('Failed to fetch observations');
        }
        const { observations: obsArr, total } =
          await observationsResponse.json();
        setObservations(obsArr);
        setTotalObservations(total);
        setObsOffset(obsArr.length);

        if (obsArr.length > 0) {
          setSelectedObservation(obsArr[0]);
        }
      } catch (err) {
        console.error('Error fetching data:', err);
        setError(err instanceof Error ? err.message : 'Failed to fetch data');
        setObservations([]);
      } finally {
        setLoading(false);
      }
    };

    fetchInitialData();
  }, []);

  // Fetch initial players and teams - EXACT SAME AS PLAYERS PAGE
  useEffect(() => {
    // Fetch teams
    fetch('/api/user/teams')
      .then(res => {
        if (!res.ok) {
          console.log('Teams API returned error status:', res.status);
          setTeams([]);
          return;
        }
        return res.json();
      })
      .then(data => {
        if (!data) return; // Skip if no data (error case)

        let arr: unknown = data;
        if (data && Array.isArray(data)) {
          arr = data;
        }
        const result = z.array(z.object({ id: z.string(), name: z.string() })).safeParse(arr);
        if (result.success) {
          // Deduplicate teams by id
          const uniqueTeams = Array.from(
            new Map(result.data.map(team => [team.id, team])).values()
          );
          uniqueTeams.sort((a, b) => a.name.localeCompare(b.name));
          setTeams(uniqueTeams);
        } else {
          console.error('Zod validation error for teams:', result.error);
          setTeams([]);
        }
      })
      .catch(error => {
        console.error('Error fetching teams:', error);
        setTeams([]);
      });

    // Fetch initial players
    setLoadingPlayers(true);
    fetch('/api/dashboard/players?offset=0&limit=10')
      .then(res => {
        if (!res.ok) {
          console.log('Players API returned error status:', res.status);
          return null;
        }
        return res.json();
      })
      .then(data => {
        if (!data) return; // Skip if no data (error case)

        if (data.players) {
          const transformedPlayers = data.players.map((player: any) => ({
            id: player.id,
            name: player.name || 'Unknown Player',
            team: player.team || 'No Team',
            status: player.status || 'active',
          }));

          // Reset the list with normalized state
          const playersMap: Record<string, SharedPlayer> = {};
          const ids: string[] = [];

          transformedPlayers.forEach((player: SharedPlayer) => {
            if (!playersMap[player.id]) {
              playersMap[player.id] = player;
              ids.push(player.id);
            }
          });

          setPlayersById(playersMap);
          setPlayerIds(ids);
          setOffset(10);
          setHasMore(data.players.length === 10);
        }
      })
      .catch(error => {
        console.error('Error fetching players:', error);
      })
      .finally(() => {
        setLoadingPlayers(false);
      });
  }, []);

  // Reset pagination when search or filter changes - EXACT SAME AS PLAYERS PAGE
  useEffect(() => {
    setOffset(0);
    setHasMore(true);
    fetchPlayers(0, true);
  }, [searchTerm, teamFilter]);

  // Reset page when player selection changes
  useEffect(() => {
    setPage(1);
  }, [selectedPlayer]);

  // Infinite scroll handler for expanded observations list
  const handleObsScroll = async () => {
    if (!showDatePicker || obsLoadingMore) return;
    const el = obsListRef.current;
    if (!el) return;
    if (el.scrollTop + el.clientHeight >= el.scrollHeight - 64) {
      // Near bottom, fetch more if available
      if (observations.length < totalObservations) {
        setObsLoadingMore(true);
        try {
          const res = await fetch(
            `/api/observations?offset=${obsOffset}&limit=${obsLimit}`
          );
          if (res.ok) {
            const data = await res.json();
            setObservations(prev => [...prev, ...data.observations]);
            setObsOffset(prev => prev + data.observations.length);
          }
        } finally {
          setObsLoadingMore(false);
        }
      }
    }
  };

  if (loading) {
    return (
      <div className="h-screen w-screen bg-[#161616] flex items-center justify-center">
        <div className="flex flex-col items-center justify-center w-full">
          <span className="text-zinc-400 text-lg font-semibold mb-4">
            Loading observations...
          </span>
          <div className="w-8 h-8 border-2 border-[#d8cc97] border-t-transparent rounded-full animate-spin"></div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen p-4 bg-[#161616] flex items-center justify-center">
        <div className="bg-red-900/20 border border-red-500 rounded p-4 text-red-300">
          {error}
        </div>
      </div>
    );
  }

  return (
    <div
      className="flex min-h-screen h-full bg-black text-white"
      style={{ background: 'black' }}
    >
      {/* Header - exact replica with coach info */}
      <header
        className="fixed top-0 left-0 w-full z-50 bg-black h-16 flex items-center px-8 border-b border-[#d8cc97] justify-between"
        style={{ boxShadow: 'none' }}
      >
        <span
          className="text-2xl font-bold tracking-wide text-[#d8cc97]"
          style={{ letterSpacing: '0.04em' }}
        >
          MP Player Development
        </span>
        <div className="flex flex-col items-end">
          <span className="text-base font-semibold text-white leading-tight">
            Coach
          </span>
          <span className="text-xs text-[#d8cc97] leading-tight">
            coach@example.com
          </span>
          <span className="text-xs text-white leading-tight">Coach</span>
        </div>
      </header>
      {/* Sidebar */}
      <Sidebar
        user={{
          name: 'Coach',
          email: 'coach@example.com',
          role: 'Coach',
        }}
      />
      {/* Main Content */}
      <div
        className="flex-1 flex ml-64 pt-16 bg-black min-h-screen"
        style={{ background: 'black', minHeight: '100vh' }}
      >
        {/* LEFT PANE: Player List - EXACT SAME AS PLAYERS PAGE */}
        <div
          className="w-1/4 border-r border-zinc-800 p-6 bg-black flex flex-col justify-start min-h-screen"
          style={{ background: 'black' }}
        >
          <div className="flex justify-between items-center mb-6">
            <h2 className="text-xl font-bold text-[#d8cc97] mt-0">Players</h2>
            <Button
              size="sm"
              onClick={() => {}}
              className="bg-[#d8cc97] text-black hover:bg-[#d8cc97]/80"
            >
              <Plus size={16} className="mr-1" />
              Add Player
            </Button>
          </div>
          {/* Search Input */}
          <div className="relative mb-6">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-zinc-400 w-4 h-4" />
            <input
              type="text"
              placeholder="Search players..."
              value={searchTerm}
              onChange={e => setSearchTerm(e.target.value)}
              className="w-full pl-10 pr-4 py-3 rounded bg-zinc-800 text-sm placeholder-gray-400 border border-zinc-700 focus:outline-none focus:border-[#d8cc97]"
            />
          </div>
          {/* Team Filter */}
          <div className="relative mb-6">
            <Filter className="absolute left-3 top-1/2 transform -translate-y-1/2 text-zinc-400 w-4 h-4" />
            <div className="relative">
              <button
                onClick={() => setIsTeamDropdownOpen(!isTeamDropdownOpen)}
                className="w-full pl-10 pr-4 py-3 rounded bg-zinc-800 text-sm text-white border border-zinc-700 focus:outline-none focus:border-[#d8cc97] flex items-center justify-between"
              >
                <span>{teamFilter === 'all' ? 'All Teams' : teamFilter}</span>
                <span className="text-zinc-400">â–¼</span>
              </button>
              {isTeamDropdownOpen && (
                <div className="absolute top-full left-0 right-0 mt-1 bg-zinc-800 border border-zinc-700 rounded shadow-lg z-10 max-h-48 overflow-y-auto">
                  <button
                    onClick={() => {
                      setTeamFilter('all');
                      setIsTeamDropdownOpen(false);
                    }}
                    className="w-full px-4 py-2 text-left text-sm text-white hover:bg-zinc-700"
                  >
                    All Teams
                  </button>
                  {teams.map(team => (
                    <button
                      key={team.id}
                      onClick={() => {
                        setTeamFilter(team.name);
                        setIsTeamDropdownOpen(false);
                      }}
                      className="w-full px-4 py-2 text-left text-sm text-white hover:bg-zinc-700"
                    >
                      {team.name}
                    </button>
                  ))}
                </div>
              )}
            </div>
          </div>
          {/* Player List - Fixed height for exactly 10 player cards */}
          <div
            className="flex-1 overflow-y-auto space-y-2"
            style={{ maxHeight: '400px' }} // Exactly 10 player cards (10 * 40px)
            onScroll={handleScroll}
          >
            {sortedPlayers.length === 0 ? (
              <div className="text-center py-8">
                <Shield className="text-zinc-700 w-12 h-12 mx-auto mb-4" />
                <p className="text-zinc-400 text-sm">No players found</p>
              </div>
            ) : (
              sortedPlayers.map((player: SharedPlayer) => (
                <div
                  key={player.id}
                  onClick={() => handlePlayerSelect(player)}
                  className={`p-3 rounded-lg cursor-pointer transition-all ${
                    selectedPlayer?.id === player.id
                      ? 'bg-[#d8cc97]/20 border border-[#d8cc97]'
                      : 'bg-zinc-800/50 border border-zinc-700 hover:bg-zinc-800 hover:border-zinc-600'
                  }`}
                >
                  <div className="flex items-center justify-between">
                    <div>
                      <p className="font-medium text-white">{player.name}</p>
                      <p className="text-sm text-zinc-400">{player.team}</p>
                    </div>
                    <div
                      className={`w-2 h-2 rounded-full ${
                        player.status === 'active'
                          ? 'bg-green-500'
                          : 'bg-zinc-500'
                      }`}
                    />
                  </div>
                </div>
              ))
            )}
          </div>
        </div>

        {/* CENTER PANE: Observations */}
        <div
          className="w-1/2 border-r border-zinc-800 p-8 bg-black flex flex-col justify-start min-h-screen"
          style={{ background: 'black' }}
        >
          <div className="flex items-center mb-6 gap-4 justify-between w-full">
            <h2 className="text-xl font-bold text-[#d8cc97] mt-0">
              {selectedPlayer
                ? `${selectedPlayer.name}'s Observations`
                : 'All Observations'}
            </h2>
            <div className="flex items-center gap-4">
              {/* Page size selector */}
              <label className="text-sm text-zinc-400">
                Show:
                <select
                  className="ml-2 px-2 py-1 rounded bg-zinc-800 text-white border border-zinc-700"
                  value={pageSize}
                  onChange={e => {
                    setPageSize(
                      e.target.value === 'all' ? 'all' : Number(e.target.value)
                    );
                    setPage(1);
                  }}
                >
                  <option value={10}>10</option>
                  <option value={25}>25</option>
                  <option value={50}>50</option>
                  <option value={100}>100</option>
                  <option value="all">All</option>
                </select>
              </label>
              {/* Date range picker toggle */}
              <Popover open={showDatePicker} onOpenChange={setShowDatePicker}>
                <PopoverTrigger asChild>
                  <Button
                    variant="outline"
                    className="px-3 py-1 border border-zinc-700 bg-zinc-800 text-[#d8cc97] hover:border-[#d8cc97]"
                  >
                    {dateRange?.from && dateRange?.to
                      ? `${dateRange.from.toLocaleDateString()} - ${dateRange.to.toLocaleDateString()}`
                      : 'Select Date Range'}
                  </Button>
                </PopoverTrigger>
                <PopoverContent className="p-4 bg-black border-[#d8cc97] text-[#d8cc97] w-auto">
                  <Calendar
                    mode="range"
                    selected={dateRange}
                    onSelect={setDateRange}
                    numberOfMonths={2}
                    className="bg-black text-[#d8cc97]"
                  />
                  <Button
                    variant="ghost"
                    className="w-full mt-2 border border-[#d8cc97] text-[#d8cc97] hover:bg-[#232323]"
                    onClick={() => setShowDatePicker(false)}
                  >
                    Close
                  </Button>
                </PopoverContent>
              </Popover>
            </div>
          </div>
          {paginatedObservations.length > 0 ? (
            <div
              ref={obsListRef}
              className="space-y-4"
              style={{
                maxHeight: `${(pageSize === 'all' ? 12 : pageSize) * 64}px`,
                minHeight: '0',
                overflowY:
                  paginatedObservations.length >
                  (pageSize === 'all' ? 12 : pageSize)
                    ? 'auto'
                    : 'visible',
              }}
              onScroll={handleObsScroll}
            >
              {paginatedObservations.map(obs => (
                <div
                  key={obs.id}
                  className="bg-zinc-800 px-6 py-3 rounded transition-all"
                  style={{ background: '#181818' }}
                >
                  <div className="flex justify-between items-start mb-3">
                    <div className="flex flex-col">
                      <div className="text-base font-bold text-[#d8cc97]">
                        {obs.playerName}
                      </div>
                      <div className="text-xs text-zinc-400">
                        {new Date(obs.date).toLocaleDateString()}
                      </div>
                    </div>
                    <div className="flex gap-3">
                      <button
                        className="text-xs text-[#d8cc97] font-semibold hover:underline bg-transparent"
                        style={{ background: 'transparent' }}
                      >
                        Edit
                      </button>
                      <button
                        className="text-xs text-red-400 font-semibold hover:underline bg-transparent"
                        style={{ background: 'transparent' }}
                      >
                        Delete
                      </button>
                    </div>
                  </div>
                  <p className="text-sm text-zinc-300 line-clamp-3">
                    {obs.description}
                  </p>
                </div>
              ))}
            </div>
          ) : (
            <div className="text-sm text-gray-500 text-center py-8">
              {selectedPlayer
                ? 'No observations found for this player.'
                : 'No observations found.'}
            </div>
          )}
          {/* Pagination controls */}
          {pageSize !== 'all' && totalPages > 1 && (
            <div className="flex justify-center mt-4 gap-2">
              <button
                className="px-2 py-1 rounded bg-zinc-800 border border-zinc-700 text-sm"
                onClick={() => setPage(p => Math.max(1, p - 1))}
                disabled={page === 1}
              >
                Prev
              </button>
              <span className="px-2 py-1 text-sm">
                Page {page} of {totalPages}
              </span>
              <button
                className="px-2 py-1 rounded bg-zinc-800 border border-zinc-700 text-sm"
                onClick={() => setPage(p => Math.min(totalPages, p + 1))}
                disabled={page === totalPages}
              >
                Next
              </button>
            </div>
          )}
        </div>

        {/* RIGHT PANE: Insights */}
        <div
          className="w-1/4 p-8 bg-black flex flex-col justify-start min-h-screen"
          style={{ background: 'black' }}
        >
          <h2 className="text-xl font-bold mb-6 text-[#d8cc97] mt-0">
            Insights
          </h2>

          {/* Teaser Feature Block */}
          <div className="bg-zinc-800 p-6 rounded">
            <div className="p-4 bg-zinc-900 rounded border border-dashed border-[#d8cc97]">
              <p className="text-sm text-[#d8cc97] font-semibold mb-3">
                ðŸš€ Coming Soon to This Panel:
              </p>
              <ul className="text-sm text-gray-400 list-disc list-inside space-y-2">
                <li>AI-powered constraint suggestions</li>
                <li>Tag trend visualizations</li>
                <li>Drill recommendations based on this observation</li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

'use client';

import React, { useState, useEffect, useCallback } from 'react';
import {
  Shield,
  Plus,
  Eye,
  Search,
  Filter,
} from 'lucide-react';
import { Sidebar } from '@/components/ui/Sidebar';
import UniversalButton from '@/components/ui/UniversalButton';
import UniversalModal from '@/components/ui/UniversalModal';
import AddPlayerModal from '@/components/basketball/AddPlayerModal';
import ArchivePlanModal from '@/components/basketball/ArchivePlanModal';
import { z } from 'zod';
import type { Player as SharedPlayer } from '@/components/basketball/PlayerListCard';

// Types for observations
interface Observation {
  id: string;
  playerId: string;
  playerFirstName?: string | undefined;
  playerLastName?: string | undefined;
  playerName: string;
  title: string;
  description: string;
  rating: number;
  date: string;
  tags: string[];
  createdAt: string;
  updatedAt: string | null;
}

interface Team {
  id: string;
  name: string;
}

// Zod schema for Observation (matching actual API response)
const ObservationSchema = z.object({
  id: z.string(),
  playerId: z.string(),
  playerFirstName: z.string().optional(),
  playerLastName: z.string().optional(),
  playerName: z.string(),
  title: z.string(),
  description: z.string(),
  rating: z.number(),
  date: z.string(),
  tags: z.array(z.string()),
  createdAt: z.string(),
  updatedAt: z.string().nullable(),
});
const ObservationsArraySchema = z.array(ObservationSchema);

// Zod schema for Team
const TeamSchema = z.object({
  id: z.string(),
  name: z.string(),
});
const TeamsArraySchema = z.array(TeamSchema);

export default function PlayersPage() {
  // Normalized state pattern - industry standard
  const [playersById, setPlayersById] = useState<Record<string, SharedPlayer>>(
    {}
  );
  const [playerIds, setPlayerIds] = useState<string[]>([]);
  const [teams, setTeams] = useState<Team[]>([]);
  const [selectedPlayer, setSelectedPlayer] = useState<SharedPlayer | null>(
    null
  );
  const [showAddPlayerModal, setShowAddPlayerModal] = useState(false);
  const [showDeletePlayerModal, setShowDeletePlayerModal] = useState(false);
  const [showArchivePlanModal, setShowArchivePlanModal] = useState(false);
  const [observations, setObservations] = useState<Observation[]>([]);
  const [loadingObservations, setLoadingObservations] = useState(true);
  const [errorObservations, setErrorObservations] = useState<string | null>(
    null
  );

  // Infinite scroll state
  const [loading, setLoading] = useState(false);
  const [hasMore, setHasMore] = useState(true);
  const [offset, setOffset] = useState(0);

  // Search and filter state
  const [searchTerm, setSearchTerm] = useState('');
  const [teamFilter, setTeamFilter] = useState('all');
  const [isTeamDropdownOpen, setIsTeamDropdownOpen] = useState(false);

  // Handler for adding a new player
  const handleAddPlayer = () => {
    setShowAddPlayerModal(true);
  };

  // Handler for selecting a player
  const handlePlayerSelect = (player: SharedPlayer) => {
    setSelectedPlayer(player);
  };

  // Handler for deleting a player
  const handleDeletePlayer = () => {
    setShowDeletePlayerModal(true);
  };

  // Handler for archiving a development plan
  const handleArchivePlan = () => {
    setShowArchivePlanModal(true);
  };

  // Handler for add player form submission
  const handleAddPlayerSubmit = (data: any) => {
    // In a real app, this would add the player to the database
    // console.log('Adding player:', data);

    // Create a new player object
    const newPlayer = {
      id: `${playerIds.length + 1}`,
      name: `${data.firstName} ${data.lastName}`,
      teamId: data.teamId,
      status: 'active' as const,
    };

    // For demo purposes, we'll just log it
    // console.log('New player would be added:', newPlayer);

    // Close the modal
    setShowAddPlayerModal(false);
  };

  // Handler for delete player confirmation
  const handleDeletePlayerConfirm = () => {
    // In a real app, this would delete the player from the database
    // console.log('Deleting player:', selectedPlayer);

    // For demo purposes, we'll just log it and clear the selection
    setSelectedPlayer(null);

    // Close the modal
    setShowDeletePlayerModal(false);
  };

  // Handler for archive plan confirmation
  const handleArchivePlanConfirm = () => {
    // In a real app, this would archive the development plan
    // console.log('Archiving development plan for player:', selectedPlayer);

    // For demo purposes, we'll just log it
    // console.log('Plan would be archived');

    // Close the modal
    setShowArchivePlanModal(false);
  };

  // Fetch players with pagination
  const fetchPlayers = useCallback(
    async (currentOffset: number = 0, reset: boolean = false) => {
      setLoading(true);
      try {
        const response = await fetch(
          `/api/dashboard/players?offset=${currentOffset}&limit=10`
        );
        const data = await response.json();

        if (data.players) {
          const transformedPlayers = data.players.map((player: any) => ({
            id: player.id,
            name: player.name || 'Unknown Player',
            team: player.team || 'No Team',
            status: player.status || 'active',
          }));

          if (reset) {
            // Reset the list with normalized state
            const playersMap: Record<string, SharedPlayer> = {};
            const ids: string[] = [];

            transformedPlayers.forEach((player: SharedPlayer) => {
              if (!playersMap[player.id]) {
                playersMap[player.id] = player;
                ids.push(player.id);
              }
            });

            setPlayersById(playersMap);
            setPlayerIds(ids);
            setOffset(10);
          } else {
            // Append to existing list with normalized state
            setPlayersById(prevPlayersById => {
              const newPlayersById = { ...prevPlayersById };
              const newIds: string[] = [];

              transformedPlayers.forEach((player: SharedPlayer) => {
                if (!newPlayersById[player.id]) {
                  newPlayersById[player.id] = player;
                  newIds.push(player.id);
                }
              });

              setPlayerIds(prevIds => [...prevIds, ...newIds]);
              return newPlayersById;
            });
            setOffset(prev => prev + 10);
          }

          setHasMore(data.players.length === 10);
        }
      } catch (error) {
        // console.error('Error fetching players:', error);
      } finally {
        setLoading(false);
      }
    },
    []
  );

  // Load more players when scrolling
  const handleScroll = useCallback(
    (e: React.UIEvent<HTMLDivElement>) => {
      const { scrollTop, scrollHeight, clientHeight } = e.currentTarget;
      if (
        scrollHeight - scrollTop <= clientHeight * 1.5 &&
        !loading &&
        hasMore
      ) {
        fetchPlayers(offset);
      }
    },
    [fetchPlayers, loading, hasMore, offset]
  );

  // Filter players based on search and team filter
  const filteredPlayers = playerIds
    .map(id => playersById[id])
    .filter(
      (player: SharedPlayer | undefined): player is SharedPlayer =>
        !!player && !!player.id
    )
    .filter(player => {
      const matchesSearch = player.name
        .toLowerCase()
        .includes(searchTerm.toLowerCase());
      const matchesTeam = teamFilter === 'all' || player.team === teamFilter;
      return matchesSearch && matchesTeam;
    });
  // Deduplicate by player.id before sorting and rendering
  const dedupedPlayers = Array.from(
    new Map(filteredPlayers.map(p => [p.id, p])).values()
  );
  const sortedPlayers = [...dedupedPlayers].sort((a, b) =>
    a.name.localeCompare(b.name)
  );
  // Debug: log duplicate IDs or undefined/null
  if (typeof window !== 'undefined') {
    const idSet = new Set();
    for (const p of sortedPlayers) {
      if (idSet.has(p.id)) {
        // console.error('DUPLICATE ID:', p.id);
      }
      idSet.add(p.id);
    }
    if (sortedPlayers.some(p => !p || !p.id)) {
      // console.error('Undefined or null player in sortedPlayers', sortedPlayers);
    }
  }

  useEffect(() => {
    // Fetch teams
    fetch('/api/user/teams')
      .then(res => {
        if (!res.ok) {
          // console.log('Teams API returned error status:', res.status);
          setTeams([]);
          return;
        }
        return res.json();
      })
      .then(data => {
        if (!data) return; // Skip if no data (error case)

        let arr: unknown = data;
        if (data && Array.isArray(data)) {
          arr = data;
        }
        const result = TeamsArraySchema.safeParse(arr);
        if (result.success) {
          // Deduplicate teams by id
          const uniqueTeams = Array.from(
            new Map(result.data.map(team => [team.id, team])).values()
          );
          uniqueTeams.sort((a, b) => a.name.localeCompare(b.name));
          setTeams(uniqueTeams);
        } else {
          // console.error('Zod validation error for teams:', result.error);
          setTeams([]);
        }
      })
      .catch(error => {
        // console.error('Error fetching teams:', error);
        setTeams([]);
      });

    // Fetch initial players
    setLoading(true);
    fetch('/api/dashboard/players?offset=0&limit=10')
      .then(res => {
        if (!res.ok) {
          // console.log('Players API returned error status:', res.status);
          return null;
        }
        return res.json();
      })
      .then(data => {
        if (!data) return; // Skip if no data (error case)

        if (data.players) {
          const transformedPlayers = data.players.map((player: any) => ({
            id: player.id,
            name: player.name || 'Unknown Player',
            team: player.team || 'No Team',
            status: player.status || 'active',
          }));

          // Reset the list with normalized state
          const playersMap: Record<string, SharedPlayer> = {};
          const ids: string[] = [];

          transformedPlayers.forEach((player: SharedPlayer) => {
            if (!playersMap[player.id]) {
              playersMap[player.id] = player;
              ids.push(player.id);
            }
          });

          setPlayersById(playersMap);
          setPlayerIds(ids);
          setOffset(10);
          setHasMore(data.players.length === 10);
        }
      })
      .catch(error => {
        // console.error('Error fetching players:', error);
      })
      .finally(() => {
        setLoading(false);
      });

    // Fetch observations
    setLoadingObservations(true);
    setErrorObservations(null);
    fetch('/api/observations')
      .then(res => {
        if (!res.ok) {
          // console.log('Observations API returned error status:', res.status);
          setObservations([]);
          setErrorObservations('Failed to fetch observations');
          return null;
        }
        return res.json();
      })
      .then(data => {
        if (!data) return; // Skip if no data (error case)

        // Handle the API response structure: { observations: [...], total: number }
        if (data && data.observations && Array.isArray(data.observations)) {
          const result = ObservationsArraySchema.safeParse(data.observations);
          if (result.success) {
            setObservations(result.data);
          } else {
            // console.error(
            //   'Zod validation error for observations:',
            //   result.error
            // );
            setObservations([]);
            setErrorObservations(
              'Invalid observations data received from API.'
            );
          }
        } else {
          // console.error(
          //   'Invalid API response structure for observations:',
          //   data
          // );
          setObservations([]);
          setErrorObservations(
            'Invalid observations data structure received from API.'
          );
        }
      })
      .catch(err => {
        // console.error('Error fetching observations:', err);
        setErrorObservations('Failed to fetch observations');
      })
      .finally(() => setLoadingObservations(false));
  }, []);

  // Reset pagination when search or filter changes
  useEffect(() => {
    setOffset(0);
    setHasMore(true);
    fetchPlayers(0, true);
  }, [searchTerm, teamFilter]);

  // Helper to get rating stars
  const getRatingStars = (rating: number | undefined) => {
    const safeRating = typeof rating === 'number' ? rating : 0;
    return Array.from({ length: 5 }, (_, i) => (
      <Star
        key={i}
        className={`h-4 w-4 ${i < safeRating ? 'text-yellow-500 fill-current' : 'text-zinc-600'}`}
      />
    ));
  };

  // Defensive: always check Array.isArray before .filter
  const safeObservations = Array.isArray(observations)
    ? observations.filter(Boolean)
    : [];
  const playerObservations = selectedPlayer
    ? safeObservations
        .filter(
          (obs): obs is Observation => !!obs && typeof obs.playerId === 'string'
        )
        .filter(obs => obs.playerId === selectedPlayer.id)
    : [];

  return (
    <div
      className="flex min-h-screen h-full bg-black text-white"
      style={{ background: 'black' }}
    >
      {/* Header - exact replica with coach info */}
      <header
        className="fixed top-0 left-0 w-full z-50 bg-black h-16 flex items-center px-8 border-b border-[#d8cc97] justify-between"
        style={{ boxShadow: 'none' }}
      >
        <span
          className="text-2xl font-bold tracking-wide text-[#d8cc97]"
          style={{ letterSpacing: '0.04em' }}
        >
          MP Player Development
        </span>
        <div className="flex flex-col items-end">
          <span className="text-base font-semibold text-white leading-tight">
            Coach
          </span>
          <span className="text-xs text-[#d8cc97] leading-tight">
            coach@example.com
          </span>
          <span className="text-xs text-white leading-tight">Coach</span>
        </div>
      </header>

      {/* Sidebar */}
      <Sidebar
        user={{
          name: 'Coach',
          email: 'coach@example.com',
          role: 'Coach',
        }}
      />

      {/* Main Content */}
      <div
        className="flex-1 flex ml-64 pt-16 bg-black min-h-screen"
        style={{ background: 'black', minHeight: '100vh' }}
      >
        {/* LEFT PANE: Player List */}
        <div
          className="w-1/4 border-r border-zinc-800 p-6 bg-black flex flex-col justify-start min-h-screen"
          style={{ background: 'black' }}
        >
          <div className="flex justify-between items-center mb-6">
            <h2 className="text-xl font-bold text-[#d8cc97] mt-0">Players</h2>
            <UniversalButton.Primary
              size="sm"
              onClick={handleAddPlayer}
              leftIcon={<Plus size={16} />}
            >
              Add Player
            </UniversalButton.Primary>
          </div>
          {/* Search Input */}
          <div className="relative mb-6">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-zinc-400 w-4 h-4" />
            <input
              type="text"
              placeholder="Search players..."
              value={searchTerm}
              onChange={e => setSearchTerm(e.target.value)}
              className="w-full pl-10 pr-4 py-3 rounded bg-zinc-800 text-sm placeholder-gray-400 border border-zinc-700 focus:outline-none focus:border-[#d8cc97]"
            />
          </div>
          {/* Team Filter */}
          <div className="relative mb-6">
            <Filter className="absolute left-3 top-1/2 transform -translate-y-1/2 text-zinc-400 w-4 h-4" />
            <div className="relative">
              <button
                onClick={() => setIsTeamDropdownOpen(!isTeamDropdownOpen)}
                className="w-full pl-10 pr-4 py-3 rounded bg-zinc-800 text-sm text-white border border-zinc-700 focus:outline-none focus:border-[#d8cc97] flex items-center justify-between"
              >
                <span>{teamFilter === 'all' ? 'All Teams' : teamFilter}</span>
                <span className="text-zinc-400">â–¼</span>
              </button>
              {isTeamDropdownOpen && (
                <div className="absolute top-full left-0 right-0 mt-1 bg-zinc-800 border border-zinc-700 rounded shadow-lg z-10 max-h-48 overflow-y-auto">
                  <button
                    onClick={() => {
                      setTeamFilter('all');
                      setIsTeamDropdownOpen(false);
                    }}
                    className="w-full px-4 py-2 text-left text-sm text-white hover:bg-zinc-700"
                  >
                    All Teams
                  </button>
                  {teams.map(team => (
                    <button
                      key={team.id}
                      onClick={() => {
                        setTeamFilter(team.name);
                        setIsTeamDropdownOpen(false);
                      }}
                      className="w-full px-4 py-2 text-left text-sm text-white hover:bg-zinc-700"
                    >
                      {team.name}
                    </button>
                  ))}
                </div>
              )}
            </div>
          </div>
          {/* Player List - Fixed height for exactly 10 player cards */}
          <div
            className="flex-1 overflow-y-auto space-y-2"
            style={{ maxHeight: '400px' }} // Exactly 10 player cards (10 * 40px)
            onScroll={handleScroll}
          >
            {sortedPlayers.length === 0 ? (
              <div className="text-center py-8">
                <Shield className="text-zinc-700 w-12 h-12 mx-auto mb-4" />
                <p className="text-zinc-400 text-sm">No players found</p>
              </div>
            ) : (
              sortedPlayers.map((player: SharedPlayer) => (
                <div
                  key={player.id}
                  onClick={() => handlePlayerSelect(player)}
                  className={`p-3 rounded-lg cursor-pointer transition-all ${
                    selectedPlayer?.id === player.id
                      ? 'bg-[#d8cc97]/20 border border-[#d8cc97]'
                      : 'bg-zinc-800/50 border border-zinc-700 hover:bg-zinc-800 hover:border-zinc-600'
                  }`}
                >
                  <div className="flex items-center justify-between">
                    <div>
                      <p className="font-medium text-white">{player.name}</p>
                      <p className="text-sm text-zinc-400">{player.team}</p>
                    </div>
                    <div
                      className={`w-2 h-2 rounded-full ${
                        player.status === 'active'
                          ? 'bg-green-500'
                          : 'bg-zinc-500'
                      }`}
                    />
                  </div>
                </div>
              ))
            )}
          </div>
        </div>

        {/* MIDDLE PANE: Player Profile */}
        <div className="flex-1 p-6 bg-black">
          {selectedPlayer ? (
            <div className="space-y-6">
              {/* Player Profile Section */}
              <div className="bg-zinc-900 border border-zinc-800 rounded-lg p-6">
                <div className="flex justify-between items-start mb-4">
                  <h3 className="text-xl font-bold text-[#d8cc97]">
                    Player Profile
                  </h3>
                  <div className="flex gap-2">
                    <UniversalButton.Secondary
                      size="sm"
                      onClick={() => {
                        /* Edit player logic */
                      }}
                    >
                      Edit Player
                    </UniversalButton.Secondary>

                    <UniversalButton.Danger
                      size="sm"
                      onClick={handleDeletePlayer}
                    >
                      Delete Player
                    </UniversalButton.Danger>
                  </div>
                </div>

                <div className="space-y-4">
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <p className="text-sm text-zinc-400">Name</p>
                      <p className="text-white">{selectedPlayer.name}</p>
                    </div>
                    <div>
                      <p className="text-sm text-zinc-400">Team</p>
                      <p className="text-white">{selectedPlayer.team}</p>
                    </div>
                  </div>

                  <div>
                    <p className="text-sm text-zinc-400">Status</p>
                    <div
                      className={`inline-flex items-center px-2 py-1 rounded-full text-xs ${
                        selectedPlayer.status === 'active'
                          ? 'bg-gold-500/20 text-gold-500'
                          : 'bg-danger-500/20 text-danger-500'
                      }`}
                    >
                      {selectedPlayer.status === 'active'
                        ? 'Active'
                        : 'Archived'}
                    </div>
                  </div>
                </div>
              </div>

              {/* Development Plan Section */}
              <div className="bg-zinc-900 border border-zinc-800 rounded-lg p-6">
                <div className="flex justify-between items-start mb-4">
                  <h3 className="text-xl font-bold text-[#d8cc97]">
                    Development Plan
                  </h3>
                  <div className="flex gap-2">
                    <UniversalButton.Primary size="sm">
                      Create Plan
                    </UniversalButton.Primary>
                    <UniversalButton.Secondary
                      size="sm"
                      onClick={handleArchivePlan}
                    >
                      Archive Plan
                    </UniversalButton.Secondary>
                  </div>
                </div>

                <div className="text-center py-8">
                  <Shield className="text-zinc-700 w-16 h-16 mx-auto mb-4" />
                  <p className="text-zinc-400 mb-2">
                    No development plan created yet
                  </p>
                  <p className="text-sm text-zinc-500">
                    Create a development plan to track this player's progress
                  </p>
                </div>
              </div>
            </div>
          ) : (
            <div className="bg-zinc-900 border border-zinc-800 rounded-lg p-6 flex flex-col items-center justify-center h-full">
              <Shield className="text-zinc-700 w-20 h-20 mb-5" />
              <h3 className="text-lg font-medium text-white mb-2">
                Select a Player to View Their Profile
              </h3>
              <p className="text-sm text-zinc-400 max-w-md mb-6 text-center">
                Select a player from the list to view their profile details and
                development plan.
              </p>
            </div>
          )}
        </div>

        {/* RIGHT PANE: Recent Observations */}
        <div
          className="w-1/3 border-l border-zinc-800 p-6 bg-black flex flex-col min-h-screen"
          style={{ background: 'black' }}
        >
          <h2 className="text-xl font-bold mb-6 text-[#d8cc97]">
            Recent Observations
          </h2>

          {selectedPlayer ? (
            <div className="flex-1 overflow-y-auto space-y-4">
              {playerObservations.length === 0 ? (
                <div className="text-center py-8">
                  <Eye className="text-zinc-700 w-12 h-12 mx-auto mb-4" />
                  <p className="text-zinc-400 text-sm">No observations yet</p>
                  <p className="text-xs text-zinc-500 mt-1">
                    Observations will appear here
                  </p>
                </div>
              ) : (
                playerObservations.slice(0, 5).map(observation => (
                  <div
                    key={observation.id}
                    className="bg-zinc-800/50 border border-zinc-700 rounded-lg p-4"
                  >
                    <div className="flex items-start justify-between mb-2">
                      <h4 className="font-medium text-white text-sm">
                        {observation.title}
                      </h4>
                      <div className="flex items-center gap-1">
                        {getRatingStars(observation.rating)}
                      </div>
                    </div>
                    <p className="text-zinc-400 text-xs mb-2 line-clamp-2">
                      {observation.description}
                    </p>
                    <div className="flex items-center justify-between">
                      <span className="text-xs text-zinc-500">
                        {new Date(observation.date).toLocaleDateString()}
                      </span>
                    </div>
                  </div>
                ))
              )}
            </div>
          ) : (
            <div className="text-center py-8">
              <Eye className="text-zinc-700 w-12 h-12 mx-auto mb-4" />
              <p className="text-zinc-400 text-sm">
                Select a player to view their observations
              </p>
            </div>
          )}
        </div>
      </div>

      {/* Modals */}
      {showAddPlayerModal && (
        <AddPlayerModal
          isOpen={showAddPlayerModal}
          onClose={() => setShowAddPlayerModal(false)}
          onSubmit={handleAddPlayerSubmit}
          teams={teams}
        />
      )}

      {showDeletePlayerModal && (
        <UniversalModal.Confirm
          open={showDeletePlayerModal}
          onOpenChange={setShowDeletePlayerModal}
          title="Delete Player"
          description={`Are you sure you want to delete ${selectedPlayer?.name}? This action cannot be undone.`}
          confirmText="Delete Player"
          onConfirm={handleDeletePlayerConfirm}
          onCancel={() => setShowDeletePlayerModal(false)}
          variant="danger"
        />
      )}

      {showArchivePlanModal && (
        <ArchivePlanModal
          isOpen={showArchivePlanModal}
          onClose={() => setShowArchivePlanModal(false)}
          onConfirm={handleArchivePlanConfirm}
        />
      )}
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import {
  ChevronDown,
  ChevronUp,
  Search,
  Filter,
  Users,
  Target,
} from 'lucide-react';
import { Sidebar } from '@/components/ui/Sidebar';
import { z } from 'zod';

// Zod schemas for validation
const SessionSchema = z.object({
  id: z.string(),
  title: z.string(),
  date: z.string(),
  time: z.string(),
  team: z.string(),
  type: z.enum([
    'Practice',
    'Game',
    'Training',
    'Meeting',
    'Workout',
    'Evaluation',
  ]),
  duration: z.number(),
  coach: z.string(),
  location: z.string(),
  description: z.string(),
  status: z.enum(['scheduled', 'in_progress', 'completed', 'cancelled']),
  attendance: z.number(),
  maxAttendance: z.number(),
  createdAt: z.string(),
  updatedAt: z.string(),
});
const SessionsArraySchema = z.array(SessionSchema);

const PlayerSchema = z.object({
  id: z.string(),
  name: z.string(),
  team: z.string(),
  status: z.string(),
});
const PlayersArraySchema = z.array(PlayerSchema);

const TeamSchema = z.object({
  id: z.string(),
  name: z.string(),
});
const TeamsArraySchema = z.array(TeamSchema);

// Types for sessions
interface Session {
  id: string;
  title: string;
  date: string;
  time: string;
  team: string;
  type: 'Practice' | 'Game' | 'Training' | 'Meeting' | 'Workout' | 'Evaluation';
  duration: number;
  coach: string;
  location: string;
  description: string;
  status: 'scheduled' | 'in_progress' | 'completed' | 'cancelled';
  attendance: number;
  maxAttendance: number;
  createdAt: string;
  updatedAt: string;
}

interface Player {
  id: string;
  name: string;
  team: string;
  status: string;
}

interface Team {
  id: string;
  name: string;
}

// Main component
export default function SessionsPage() {
  const [sessions, setSessions] = useState<Session[]>([]);
  const [selectedSession, setSelectedSession] = useState<Session | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  // Pagination state for sessions
  const [page, setPage] = useState(1);
  const pageSize = 5;

  // Player/team data for left column
  const [players, setPlayers] = useState<Player[]>([]);
  const [teams, setTeams] = useState<Team[]>([]);
  const [selectedSessionId, setSelectedSessionId] = useState<string | null>(
    null
  );
  const [searchTerm, setSearchTerm] = useState('');
  const [teamFilter, setTeamFilter] = useState('all');
  const [isTeamDropdownOpen, setIsTeamDropdownOpen] = useState(false);

  // Filter sessions by selected team
  const filteredSessions = selectedSessionId
    ? sessions.filter(session => session.id === selectedSessionId)
    : sessions;

  const filteredSessionsList = sessions.filter(session => {
    const matchesSearch = session.title
      .toLowerCase()
      .includes(searchTerm.toLowerCase());
    const matchesTeam = teamFilter === 'all' || session.team === teamFilter;
    return matchesSearch && matchesTeam;
  });

  // Handle session selection with toggle functionality
  const handleSessionSelect = (sessionId: string) => {
    if (selectedSessionId === sessionId) {
      // Clicking the same session again - show all sessions
      setSelectedSessionId(null);
    } else {
      // Clicking a different session - filter to their details
      setSelectedSessionId(sessionId);
    }
  };

  // Fetch real data
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        setError(null);

        // Fetch sessions from API with validation
        const sessionsResponse = await fetch('/api/dashboard/sessions');
        let transformedSessions: Session[] = [];
        if (sessionsResponse.ok) {
          const rawSessionsData = await sessionsResponse.json();

          // Transform the raw data to match our schema
          const transformedRawSessions = rawSessionsData.map(
            (session: any) => ({
              id: session.id,
              title: session.title,
              date: session.date,
              time: session.time,
              team: session.team,
              type: session.type,
              duration: 90, // Default duration
              coach: 'Coach Johnson', // Default coach
              location: 'Main Gym',
              description: `${session.type} session for ${session.team}`,
              status: 'scheduled' as const,
              attendance: Math.floor(Math.random() * 20) + 5,
              maxAttendance: 25,
              createdAt: '2024-01-01',
              updatedAt: '2024-01-15',
            })
          );

          // Validate sessions data
          const validatedSessions = SessionsArraySchema.safeParse(
            transformedRawSessions
          );
          if (!validatedSessions.success) {
            console.error('Invalid sessions data:', validatedSessions.error);
            throw new Error('Invalid sessions data received');
          }

          // Filter out any invalid sessions
          const validSessions = validatedSessions.data.filter(
            (session): session is Session =>
              session &&
              typeof session === 'object' &&
              typeof session.id === 'string' &&
              typeof session.title === 'string' &&
              typeof session.team === 'string' &&
              session.id.trim() !== '' &&
              session.title.trim() !== '' &&
              session.team.trim() !== ''
          );

          // Deduplicate sessions by id
          const uniqueSessions = Array.from(
            new Map(
              validSessions.map(session => [session.id, session])
            ).values()
          );
          setSessions(uniqueSessions);
          transformedSessions = uniqueSessions;
        }

        // Fetch players with validation
        const playersResponse = await fetch(
          '/api/dashboard/players?offset=0&limit=10'
        );
        if (playersResponse.ok) {
          const rawPlayersData = await playersResponse.json();
          // Handle the API response structure: { players: [...], total: number }
          if (
            rawPlayersData &&
            rawPlayersData.players &&
            Array.isArray(rawPlayersData.players)
          ) {
            const transformedRawPlayers = rawPlayersData.players.map(
