import { getUser } from '@/lib/db/queries';
import { db } from '@/lib/db/drizzle';
import { mpCorePerson, mpCorePersonGroup, mpCoreGroup } from '@/lib/db/schema';
import { eq, and, isNotNull } from 'drizzle-orm';

export async function GET(
  request: Request,
  { params }: { params: Promise<{ id: string }> }
) {
  try {
    const user = await getUser();

    if (!user) {
      return Response.json({ error: 'User not found' }, { status: 404 });
    }

    const { id: teamId } = await params;

    if (!db) {
      return Response.json(
        { error: 'Database not available' },
        { status: 500 }
      );
    }

    // Get players for the specific team with their group/team info
    const players = await db
      .select({
        id: mpCorePerson.id,
        firstName: mpCorePerson.firstName,
        lastName: mpCorePerson.lastName,
        email: mpCorePerson.email,
        teamId: mpCoreGroup.id,
        teamName: mpCoreGroup.name,
        position: mpCorePersonGroup.position,
        role: mpCorePersonGroup.role,
        personType: mpCorePerson.personType,
        identifier: mpCorePersonGroup.identifier,
        cycleName: mpCorePersonGroup.cycleId, // or join to cycle table if needed
      })
      .from(mpCorePerson)
      .innerJoin(
        mpCorePersonGroup,
        eq(mpCorePerson.id, mpCorePersonGroup.personId)
      )
      .innerJoin(mpCoreGroup, eq(mpCorePersonGroup.groupId, mpCoreGroup.id))
      .where(
        and(
          eq(mpCorePersonGroup.groupId, teamId),
          eq(mpCorePerson.personType, 'player')
        )
      );

    // Transform the data to match the expected format
    const formattedPlayers = players.map(player => ({
      id: player.id,
      displayName:
        player.firstName && player.lastName
          ? `${player.firstName} ${player.lastName}`.trim()
          : player.firstName || player.lastName || 'Unknown Player',
      teamId: player.teamId,
      personType: player.personType || 'player',
      position: player.position || 'Unknown',
    }));

    return Response.json(formattedPlayers);
  } catch (error) {
    console.error('Error fetching team players:', error);
    return Response.json(
      { error: 'Failed to fetch team players' },
      { status: 500 }
    );
  }
}

import { getUser } from '@/lib/db/queries';
import { db } from '@/lib/db/drizzle';
import { mpCorePerson, mpCorePersonGroup, mpCoreGroup } from '@/lib/db/schema';
import { and, isNotNull, eq } from 'drizzle-orm';

export async function GET() {
  try {
    const user = await getUser();

    if (!user) {
      return Response.json({ error: 'User not found' }, { status: 404 });
    }

    if (!db) {
      return Response.json(
        { error: 'Database not available' },
        { status: 500 }
      );
    }

    // Get all teams from the normalized schema
    const teams = await db
      .select({
        id: mpCoreGroup.id,
        name: mpCoreGroup.name,
        coachFirstName: mpCorePerson.firstName,
        coachLastName: mpCorePerson.lastName,
        role: mpCorePersonGroup.role,
        personType: mpCorePerson.personType,
        email: mpCorePerson.email,
      })
      .from(mpCoreGroup)
      .innerJoin(
        mpCorePersonGroup,
        eq(mpCoreGroup.id, mpCorePersonGroup.groupId)
      )
      .innerJoin(mpCorePerson, eq(mpCorePersonGroup.personId, mpCorePerson.id))
      .where(
        and(isNotNull(mpCorePersonGroup.groupId), isNotNull(mpCoreGroup.name))
      )
      .groupBy(
        mpCoreGroup.id,
        mpCoreGroup.name,
        mpCorePerson.firstName,
        mpCorePerson.lastName,
        mpCorePersonGroup.role,
        mpCorePerson.personType,
        mpCorePerson.email
      );

    // Transform the data to match the expected format
    const formattedTeams = teams.map(team => ({
      id: team.id || 'unknown',
      name: team.name || 'Unknown Team',
      coachName:
        team.coachFirstName && team.coachLastName
          ? `${team.coachFirstName} ${team.coachLastName}`.trim()
          : team.coachFirstName || team.coachLastName || 'Unknown Coach',
      role: team.role || 'member',
      personType: team.personType || 'player',
      email: team.email,
      createdAt: new Date().toISOString(), // Since we don't have this in the current schema
      updatedAt: new Date().toISOString(), // Since we don't have this in the current schema
    }));

    return Response.json(formattedTeams);
  } catch (error) {
    console.error('Error fetching teams:', error);
    return Response.json({ error: 'Failed to fetch teams' }, { status: 500 });
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { db } from '@/lib/db/drizzle';
import { mpCorePerson } from '@/lib/db/schema';

export async function GET() {
  try {
    if (!db) {
      return NextResponse.json(
        { error: 'Database connection not available' },
        { status: 500 }
      );
    }

    // Get all users (limit to 10 for security)
    const users = await db
      .select({
        id: mpCorePerson.id,
        email: mpCorePerson.email,
        displayName: mpCorePerson.displayName,
        firstName: mpCorePerson.firstName,
        authUid: mpCorePerson.authUid,
      })
      .from(mpCorePerson)
      .limit(10);

    return NextResponse.json({
      success: true,
      userCount: users.length,
      users: users.map(user => ({
        id: user.id,
        email: user.email,
        displayName: user.displayName,
        firstName: user.firstName,
        hasAuthUid: !!user.authUid,
      })),
    });
  } catch (error) {
    console.error('Database test error:', error);
    return NextResponse.json(
      {
        error: 'Database test failed',
        details: error instanceof Error ? error.message : 'Unknown error',
      },
      { status: 500 }
    );
  }
}

