    icon: <Settings size={20} />,
  },
];

export function Sidebar({ onSignOut }: SidebarProps) {
  const pathname = usePathname();
  const [isMobileOpen, setIsMobileOpen] = useState(false);
  const [expandedItems, setExpandedItems] = useState<Record<string, boolean>>(
    {}
  );

  // Close mobile sidebar when pathname changes
  useEffect(() => {
    setIsMobileOpen(false);
  }, [pathname]);

  // Pre-expand the item that contains the current path
  useEffect(() => {
    navItems.forEach(item => {
      if (item.children) {
        const shouldExpand = item.children.some(child =>
          pathname.startsWith(child.href)
        );
        if (shouldExpand) {
          setExpandedItems(prev => ({ ...prev, [item.title]: true }));
        }
      }
    });
  }, [pathname]);

  const toggleExpand = (title: string) => {
    setExpandedItems(prev => ({
      ...prev,
      [title]: !prev[title],
    }));
  };

  const isActive = (href: string) => {
    return pathname === href || pathname.startsWith(`${href}/`);
  };

  return (
    <>
      {/* Mobile Toggle Button */}
      <div className="fixed top-4 left-4 z-50 md:hidden">
        <button
          onClick={() => setIsMobileOpen(!isMobileOpen)}
          className="p-2 rounded-md bg-zinc-800 text-white hover:bg-zinc-700 focus:outline-none focus:ring-2 focus:ring-gold-500"
          aria-label="Toggle sidebar"
        >
          {isMobileOpen ? <X size={20} /> : <Menu size={20} />}
        </button>
      </div>

      {/* Overlay for mobile */}
      {isMobileOpen && (
        <div
          className="fixed inset-0 bg-black/50 z-40 md:hidden"
          onClick={() => setIsMobileOpen(false)}
        />
      )}

      {/* Sidebar */}
      <aside
        className={cn(
          'fixed top-0 left-0 z-40 h-screen bg-zinc-900 border-r border-zinc-800 transition-all duration-300 ease-in-out',
          'w-64 md:w-64 md:translate-x-0',
          isMobileOpen ? 'translate-x-0' : '-translate-x-full'
        )}
      >
        {/* Logo & Brand */}
        <div className="flex items-center justify-center h-16 border-b border-zinc-800">
          <Link href="/dashboard" className="flex items-center gap-2">
            <Award size={24} className="text-gold-500" />
            <span className="text-lg font-semibold text-white">
              MP Basketball
            </span>
          </Link>
        </div>

        {/* Navigation */}
        <nav className="px-3 py-4 overflow-y-auto h-[calc(100vh-16rem)]">
          <ul className="space-y-1">
            {navItems.map(item => (
              <li key={item.title}>
                {item.children ? (
                  <div>
                    <button
                      onClick={() => toggleExpand(item.title)}
                      className={cn(
                        'flex items-center justify-between w-full px-3 py-2 rounded-md text-sm font-medium',
                        isActive(item.href)
                          ? 'bg-gold-500/20 text-gold-500'
                          : 'text-zinc-300 hover:bg-zinc-800 hover:text-white'
                      )}
                    >
                      <div className="flex items-center">
                        <span className="mr-3">{item.icon}</span>
                        {item.title}
                      </div>
                      {expandedItems[item.title] ? (
                        <ChevronDown size={16} />
                      ) : (
                        <ChevronRight size={16} />
                      )}
                    </button>
                    {expandedItems[item.title] && (
                      <ul className="pl-10 mt-1 space-y-1">
                        {item.children.map(child => (
                          <li key={child.title}>
                            <Link
                              href={child.href}
                              className={cn(
                                'flex items-center px-3 py-2 rounded-md text-sm font-medium',
                                isActive(child.href)
                                  ? 'bg-gold-500/20 text-gold-500'
                                  : 'text-zinc-400 hover:bg-zinc-800 hover:text-white'
                              )}
                            >
                              <span className="mr-2">{child.icon}</span>
                              {child.title}
                            </Link>
                          </li>
                        ))}
                      </ul>
                    )}
                  </div>
                ) : (
                  <Link
                    href={item.href}
                    className={cn(
                      'flex items-center px-3 py-2 rounded-md text-sm font-medium',
                      isActive(item.href)
                        ? 'bg-gold-500/20 text-gold-500'
                        : 'text-zinc-300 hover:bg-zinc-800 hover:text-white'
                    )}
                  >
                    <span className="mr-3">{item.icon}</span>
                    {item.title}
                  </Link>
                )}
              </li>
            ))}
          </ul>
        </nav>

        {/* Footer */}
        <div className="absolute bottom-0 left-0 right-0 p-4 border-t border-zinc-800">
          {onSignOut && (
            <UniversalButton.Ghost
              onClick={onSignOut}
              className="w-full justify-start text-zinc-400 hover:text-white"
              leftIcon={<LogOut size={18} />}
            >
              Sign Out
            </UniversalButton.Ghost>
          )}
        </div>
      </aside>
    </>
  );
}

export default Sidebar;

import React, { forwardRef, ButtonHTMLAttributes, ReactNode } from 'react';
import { cva, type VariantProps } from 'class-variance-authority';
import { Loader2 } from 'lucide-react';
import { cn } from '@/lib/utils';

// Button variants using class-variance-authority
const buttonVariants = cva(
  'inline-flex items-center justify-center gap-2 whitespace-nowrap rounded-md text-sm font-medium transition-all focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-offset-2 disabled:pointer-events-none disabled:opacity-50',
  {
    variants: {
      variant: {
        // Solid variants
        primary:
          'bg-gold-500 text-black hover:bg-gold-600 focus-visible:ring-gold-500/50',
        danger:
          'bg-danger-500 text-white hover:bg-danger-600 focus-visible:ring-danger-500/50',
        success:
          'bg-success-500 text-white hover:bg-success-600 focus-visible:ring-success-500/50',
        warning:
          'bg-warning-500 text-black hover:bg-warning-600 focus-visible:ring-warning-500/50',
        archive:
          'bg-archive-500 text-white hover:bg-archive-600 focus-visible:ring-archive-500/50',
        gray: 'bg-gray-500 text-white hover:bg-gray-600 focus-visible:ring-gray-500/50',

        // Outline variants
        primaryOutline:
          'border border-gold-500 text-gold-500 bg-transparent hover:bg-gold-500/10 focus-visible:ring-gold-500/50',
        dangerOutline:
          'border border-danger-500 text-danger-500 bg-transparent hover:bg-danger-500/10 focus-visible:ring-danger-500/50',
        successOutline:
          'border border-success-500 text-success-500 bg-transparent hover:bg-success-500/10 focus-visible:ring-success-500/50',
        warningOutline:
          'border border-warning-500 text-warning-500 bg-transparent hover:bg-warning-500/10 focus-visible:ring-warning-500/50',
        archiveOutline:
          'border border-archive-500 text-archive-500 bg-transparent hover:bg-archive-500/10 focus-visible:ring-archive-500/50',
        grayOutline:
          'border border-gray-500 text-gray-500 bg-transparent hover:bg-gray-500/10 focus-visible:ring-gray-500/50',

        // Ghost and text variants
        ghost:
          'bg-transparent text-gray-500 hover:bg-gray-500/10 hover:text-gray-700 focus-visible:ring-gray-500/50',
        text: 'bg-transparent text-gold-500 hover:underline focus-visible:ring-gold-500/50 p-0',
      },
      size: {
        xs: 'h-7 px-2 text-xs',
        sm: 'h-8 px-3 text-sm',
        md: 'h-10 px-4',
        lg: 'h-11 px-6',
        xl: 'h-12 px-8 text-lg',
        icon: 'h-10 w-10 p-2',
      },
    },
    defaultVariants: {
      variant: 'primary',
      size: 'md',
    },
  }
);

// Props interface extending button HTML attributes and variants
export interface ButtonProps
  extends ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof buttonVariants> {
  children: ReactNode;
  loading?: boolean;
  leftIcon?: ReactNode;
  rightIcon?: ReactNode;
  fullWidth?: boolean;
}

// Base Button component with ref forwarding
const Button = forwardRef<HTMLButtonElement, ButtonProps>(
  (
    {
      className,
      variant,
      size,
      children,
      loading = false,
      disabled,
      leftIcon,
      rightIcon,
      fullWidth = false,
      ...props
    },
    ref
  ) => {
    return (
      <button
        className={cn(
          buttonVariants({ variant, size }),
          fullWidth ? 'w-full' : '',
          className
        )}
        ref={ref}
        disabled={disabled || loading}
        {...props}
      >
        {loading ? (
          <Loader2 className="mr-2 h-4 w-4 animate-spin" />
        ) : (
          leftIcon && <span className="mr-1">{leftIcon}</span>
        )}
        {children}
        {rightIcon && !loading && <span className="ml-1">{rightIcon}</span>}
      </button>
    );
  }
);

Button.displayName = 'Button';

// Create specialized button components
const UniversalButton = {
  // Base component for custom styling
  Base: Button,

  // Solid variants
  Primary: forwardRef<HTMLButtonElement, Omit<ButtonProps, 'variant'>>(
    (props, ref) => <Button variant="primary" ref={ref} {...props} />
  ),
  Danger: forwardRef<HTMLButtonElement, Omit<ButtonProps, 'variant'>>(
    (props, ref) => <Button variant="danger" ref={ref} {...props} />
  ),
  Success: forwardRef<HTMLButtonElement, Omit<ButtonProps, 'variant'>>(
    (props, ref) => <Button variant="success" ref={ref} {...props} />
  ),
  Warning: forwardRef<HTMLButtonElement, Omit<ButtonProps, 'variant'>>(
    (props, ref) => <Button variant="warning" ref={ref} {...props} />
  ),
  Archive: forwardRef<HTMLButtonElement, Omit<ButtonProps, 'variant'>>(
    (props, ref) => <Button variant="archive" ref={ref} {...props} />
  ),
  Gray: forwardRef<HTMLButtonElement, Omit<ButtonProps, 'variant'>>(
    (props, ref) => <Button variant="gray" ref={ref} {...props} />
  ),

  // Outline variants
  Secondary: forwardRef<HTMLButtonElement, Omit<ButtonProps, 'variant'>>(
    (props, ref) => <Button variant="primaryOutline" ref={ref} {...props} />
  ),
  DangerOutline: forwardRef<HTMLButtonElement, Omit<ButtonProps, 'variant'>>(
    (props, ref) => <Button variant="dangerOutline" ref={ref} {...props} />
  ),
  SuccessOutline: forwardRef<HTMLButtonElement, Omit<ButtonProps, 'variant'>>(
    (props, ref) => <Button variant="successOutline" ref={ref} {...props} />
  ),
  WarningOutline: forwardRef<HTMLButtonElement, Omit<ButtonProps, 'variant'>>(
    (props, ref) => <Button variant="warningOutline" ref={ref} {...props} />
  ),
  ArchiveOutline: forwardRef<HTMLButtonElement, Omit<ButtonProps, 'variant'>>(
    (props, ref) => <Button variant="archiveOutline" ref={ref} {...props} />
  ),
  GrayOutline: forwardRef<HTMLButtonElement, Omit<ButtonProps, 'variant'>>(
    (props, ref) => <Button variant="grayOutline" ref={ref} {...props} />
  ),

  // Ghost and text variants
  Ghost: forwardRef<HTMLButtonElement, Omit<ButtonProps, 'variant'>>(
    (props, ref) => <Button variant="ghost" ref={ref} {...props} />
  ),
  Text: forwardRef<HTMLButtonElement, Omit<ButtonProps, 'variant'>>(
    (props, ref) => <Button variant="text" ref={ref} {...props} />
  ),
};

// Add display names for all components
Object.keys(UniversalButton).forEach(key => {
  if (key !== 'Base') {
    (UniversalButton as any)[key].displayName = `UniversalButton.${key}`;
  }
});

export { UniversalButton, buttonVariants };
export default UniversalButton;

