// Base modal props
interface BaseModalProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  title: string;
  description?: string;
  children?: ReactNode;
  variant?: 'default' | 'danger' | 'success' | 'warning' | 'archive' | 'gray';
  size?: 'sm' | 'md' | 'lg' | 'xl';
  className?: string;
  showCloseButton?: boolean;
}

// Confirmation modal props
interface ConfirmationModalProps extends BaseModalProps {
  onConfirm: () => void;
  onCancel?: () => void;
  confirmText?: string;
  cancelText?: string;
  loading?: boolean;
}

// Form modal props
interface FormModalProps extends BaseModalProps {
  onSubmit: () => void;
  onCancel?: () => void;
  submitText?: string;
  cancelText?: string;
  loading?: boolean;
  disabled?: boolean;
}

// Basic modal component
const GoldModal = ({
  open,
  onOpenChange,
  title,
  description,
  children,
  variant = 'default',
  size = 'md',
  className,
  showCloseButton = true,
}: BaseModalProps) => {
  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent
        className={cn(modalVariants({ variant, size }), className)}
        onInteractOutside={e => e.preventDefault()}
      >
        <DialogHeader className={modalHeaderVariants({ variant })}>
          <DialogTitle className={modalTitleVariants({ variant })}>
            {title}
          </DialogTitle>
          {description && (
            <DialogDescription className="text-zinc-400">
              {description}
            </DialogDescription>
          )}
        </DialogHeader>
        {children}
        {showCloseButton && (
          <button
            className="absolute right-4 top-4 rounded-sm opacity-70 ring-offset-zinc-900 transition-opacity hover:opacity-100 focus:outline-none focus:ring-2 focus:ring-zinc-700 focus:ring-offset-2 disabled:pointer-events-none data-[state=open]:bg-zinc-800"
            onClick={() => onOpenChange(false)}
          >
            <X className="h-4 w-4" />
            <span className="sr-only">Close</span>
          </button>
        )}
      </DialogContent>
    </Dialog>
  );
};

// Confirmation modal component
const ConfirmationModal = ({
  open,
  onOpenChange,
  title,
  description,
  onConfirm,
  onCancel,
  confirmText = 'Confirm',
  cancelText = 'Cancel',
  variant = 'default',
  loading = false,
  children,
  ...props
}: ConfirmationModalProps & { children?: ReactNode }) => {
  // Map variant to button variant
  const buttonVariantMap = {
    default: 'primary',
    danger: 'danger',
    success: 'success',
    warning: 'warning',
    archive: 'archive',
    gray: 'gray',
  } as const;

  const buttonVariant = buttonVariantMap[variant] || 'primary';

  return (
    <GoldModal
      open={open}
      onOpenChange={onOpenChange}
      title={title}
      {...(description ? { description } : {})}
      variant={variant}
      showCloseButton={false}
      {...props}
    >
      {children}
      <DialogFooter className={modalFooterVariants({ variant })}>
        {onCancel && (
          <UniversalButton.Ghost
            onClick={() => {
              onCancel();
              onOpenChange(false);
            }}
            size="sm"
          >
            {cancelText}
          </UniversalButton.Ghost>
        )}
        <UniversalButton.Base
          variant={buttonVariant}
          onClick={() => {
            onConfirm();
            onOpenChange(false);
          }}
          loading={loading}
          size="sm"
        >
          {confirmText}
        </UniversalButton.Base>
      </DialogFooter>
    </GoldModal>
  );
};

// Form modal component
const FormModal = ({
  open,
  onOpenChange,
  title,
  description,
  children,
  onSubmit,
  onCancel,
  submitText = 'Submit',
  cancelText = 'Cancel',
  variant = 'default',
  loading = false,
  disabled = false,
  ...props
}: FormModalProps) => {
  // Map variant to button variant
  const buttonVariantMap = {
    default: 'primary',
    danger: 'danger',
    success: 'success',
    warning: 'warning',
    archive: 'archive',
    gray: 'gray',
  } as const;

  const buttonVariant = buttonVariantMap[variant] || 'primary';

  return (
    <GoldModal
      open={open}
      onOpenChange={onOpenChange}
      title={title}
      {...(description ? { description } : {})}
      variant={variant}
      showCloseButton={false}
      {...props}
    >
      <form
        onSubmit={e => {
          e.preventDefault();
          onSubmit();
          onOpenChange(false);
        }}
      >
        {children}
        <DialogFooter className={modalFooterVariants({ variant })}>
          {onCancel && (
            <UniversalButton.Ghost
              type="button"
              onClick={() => {
                onCancel();
                onOpenChange(false);
              }}
              size="sm"
            >
              {cancelText}
            </UniversalButton.Ghost>
          )}
          <UniversalButton.Base
            type="submit"
            variant={buttonVariant}
            loading={loading}
            disabled={disabled}
            size="sm"
          >
            {submitText}
          </UniversalButton.Base>
        </DialogFooter>
      </form>
    </GoldModal>
  );
};

// Unified modal component
export const UniversalModal = {
  // Basic modal for content display
  Basic: GoldModal,

  // Confirmation modal for user confirmations
  Confirm: ConfirmationModal,

  // Form modal for data entry
  Form: FormModal,

  // Unified API for all modal types
  Dialog: ({
    type = 'basic',
    variant = 'default',
    ...props
  }: BaseModalProps & {
    type?: 'basic' | 'confirmation' | 'form';
  } & Partial<ConfirmationModalProps> &
    Partial<FormModalProps>) => {
    switch (type) {
      case 'confirmation':
        return (
          <ConfirmationModal
            variant={variant}
            {...(props as ConfirmationModalProps)}
          />
        );
      case 'form':
        return <FormModal variant={variant} {...(props as FormModalProps)} />;
      default:
        return <GoldModal variant={variant} {...(props as BaseModalProps)} />;
    }
  },
};

