        cancelText={cancelText}
        variant="default"
        loading={loading}
        disabled={disabled}
        {...props}
      >
        {children}
      </UniversalModal.Form>
    );
  },

  // Info modal
  Info: ({
    open,
    onOpenChange,
    title,
    description,
    children,
    variant = 'default',
    size = 'md',
    ...props
  }: BaseModalProps) => {
    return (
      <UniversalModal.Basic
        open={open}
        onOpenChange={onOpenChange}
        title={title}
        {...(description ? { description } : {})}
        variant={variant}
        size={size}
        {...props}
      >
        {children}
      </UniversalModal.Basic>
    );
  },
};

// Export the main components
export default UniversalModal;

