export const mpbcPerson = pgTable(
  'mpbc_person',
  {
    id: uuid().primaryKey().notNull(),
    mpCorePersonId: uuid('mp_core_person_id').notNull(),
    basketballAdvancementLevel: text('basketball_advancement_level'),
