import { createClient as createSupabaseClient } from '@supabase/supabase-js';
import { type SupabaseClient } from '@supabase/supabase-js';
import { signIn, signUp } from '@/app/(login)/actions';

// Initialize the Supabase client for browser usage
let supabaseClient: SupabaseClient | null = null;

/**
 * Creates and returns a Supabase client for browser-side usage
 */
export const createClient = () => {
  if (supabaseClient) return supabaseClient;

  const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL!;
  const supabaseAnonKey = process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!;

  if (!supabaseUrl || !supabaseAnonKey) {
    throw new Error('Supabase URL and Anon Key must be provided');
  }

  supabaseClient = createSupabaseClient(supabaseUrl, supabaseAnonKey, {
    auth: {
      persistSession: true,
      autoRefreshToken: true,
      detectSessionInUrl: true,
    },
  });

  return supabaseClient;
};

/**
 * Sign in with email and password
 * This handles both Supabase authentication and our custom session management
 */
export const signInWithEmail = async (email: string, password: string) => {
  const supabase = createClient();

  // First authenticate with Supabase
  const { data, error } = await supabase.auth.signInWithPassword({
    email,
    password,
  });

  if (error) {
    return { error: error.message };
  }

  // Then create our custom session
  const formData = new FormData();
  formData.append('email', email);

  // Use our server action to create a session
  return signIn({}, formData);
};

/**
 * Sign up with email and password
 * This handles both Supabase user creation and our custom session management
 */
export const signUpWithEmail = async (
  email: string,
  password: string,
  displayName?: string,
  inviteId?: string
) => {
  const supabase = createClient();

  // First create a user in Supabase
  const { data, error } = await supabase.auth.signUp({
    email,
    password,
    options: {
      emailRedirectTo: `${window.location.origin}/auth/confirm`,
      data: {
        display_name: displayName || email,
      },
    },
  });

  if (error) {
    return { error: error.message };
  }

  if (data.user) {
    // Then create our custom session
    const formData = new FormData();
    formData.append('email', email);
    formData.append('authUid', data.user.id);

    if (displayName) {
      formData.append('displayName', displayName);
    }

    if (inviteId) {
      formData.append('inviteId', inviteId);
    }

    // Use our server action to create a session
    return signUp({}, formData);
  }

  return { error: 'Failed to create user' };
};

/**
 * Sign out from both Supabase and our custom session
 */
export const signOutUser = async () => {
  const supabase = createClient();

  // Sign out from Supabase
  await supabase.auth.signOut();

  // Our server action will handle clearing the custom session cookie
  // and redirecting to the sign-in page
  window.location.href = '/sign-in';
};

/**
 * Get the current Supabase session
 */
export const getSupabaseSession = async () => {
  const supabase = createClient();
  const { data, error } = await supabase.auth.getSession();

  if (error) {
    console.error('Error getting Supabase session:', error);
    return null;
  }

  return data.session;
};

/**
 * Get the current Supabase user
 */
export const getSupabaseUser = async () => {
  const session = await getSupabaseSession();
  return session?.user || null;
};

/**
 * Reset password
 */
export const resetPassword = async (email: string) => {
  const supabase = createClient();

  const { data, error } = await supabase.auth.resetPasswordForEmail(email, {
    redirectTo: `${window.location.origin}/auth/update-password`,
  });

  if (error) {
    return { error: error.message };
  }

  return { success: true };
};

/**
 * Update password
 */
export const updatePassword = async (newPassword: string) => {
  const supabase = createClient();

  const { data, error } = await supabase.auth.updateUser({
    password: newPassword,
  });

  if (error) {
    return { error: error.message };
  }

  return { success: true };
};

export default {
  createClient,
  signInWithEmail,
  signUpWithEmail,
  signOutUser,
  getSupabaseSession,
  getSupabaseUser,
  resetPassword,
  updatePassword,
};

import { createServerClient, type CookieOptions } from '@supabase/ssr';
import { NextResponse, type NextRequest } from 'next/server';

/**
 * Creates a Supabase client for use in the Next.js middleware
 * Handles cookie management for Supabase auth sessions
 *
 * @param request The incoming request
 * @returns The Supabase client and a modified response with updated cookies
 */
export const createClient = (request: NextRequest) => {
  // Create an unmodified response
  let response = NextResponse.next({
    request: {
      headers: request.headers,
    },
  });

  // Create the Supabase client with cookie handling
  const supabase = createServerClient(
    process.env.NEXT_PUBLIC_SUPABASE_URL!,
    process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!,
    {
      cookies: {
        get(name: string) {
          return request.cookies.get(name)?.value;
        },
        set(name: string, value: string, options: CookieOptions) {
          // If the cookie is updated, update the cookies for the request and response
          request.cookies.set({
            name,
            value,
            ...options,
          });
          response = NextResponse.next({
            request: {
              headers: request.headers,
            },
          });
          response.cookies.set({
            name,
            value,
            ...options,
          });
        },
        remove(name: string, options: CookieOptions) {
          // If the cookie is removed, update the cookies for the request and response
          request.cookies.set({
            name,
            value: '',
            ...options,
          });
          response = NextResponse.next({
            request: {
              headers: request.headers,
            },
          });
          response.cookies.set({
            name,
            value: '',
            ...options,
          });
        },
      },
    }
  );

  return { supabase, response };
};

/**
 * Refreshes the Supabase session in middleware if needed
 * This should be called in the middleware to ensure the Supabase session
 * is always valid before proceeding with the request
 *
 * @param request The incoming request
 * @returns A response with updated cookies if the session was refreshed, or null if no refresh was needed
 */
export const refreshSession = async (request: NextRequest) => {
  const { supabase, response } = createClient(request);

  // Get the session from Supabase
  const {
    data: { session },
  } = await supabase.auth.getSession();

  // If no session or session doesn't need refresh, return null
  if (!session) {
    return null;
  }

  return response;
};

export default {
  createClient,
  refreshSession,
};

import { createClient as createSupabaseClient } from '@supabase/supabase-js';
import { cookies } from 'next/headers';
import { type SupabaseClient } from '@supabase/supabase-js';
import { cache } from 'react';

/**
 * Creates a Supabase client for server-side operations
 * This is cached to avoid creating multiple clients during a request
 */
export const createClient = cache(() => {
  const cookieStore = cookies();
  const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL!;
  const supabaseAnonKey = process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!;
  const supabaseServiceKey = process.env.SUPABASE_SERVICE_ROLE_KEY;

  if (!supabaseUrl || !supabaseAnonKey) {
    throw new Error('Supabase URL and Anon Key must be provided');
  }

  // Use the service role key if available for admin operations
  // Otherwise use the anon key
  const supabaseKey = supabaseServiceKey || supabaseAnonKey;

  return createSupabaseClient(supabaseUrl, supabaseKey, {
    auth: {
      persistSession: false, // We don't want to persist the session on server
      autoRefreshToken: false,
      detectSessionInUrl: false,
    },
    cookies: {
      get(name: string) {
        return cookieStore.get(name)?.value;
      },
      set(name: string, value: string, options: any) {
        // We don't set cookies from server components
        // This is handled by our custom session management
      },
      remove(name: string, options: any) {
        // We don't remove cookies from server components
        // This is handled by our custom session management
      },
    },
  });
});

/**
 * Gets a Supabase admin client with the service role key
 * This should only be used in trusted server contexts like API routes
 * and never exposed to the client
 */
export const createAdminClient = () => {
  const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL!;
  const supabaseServiceKey = process.env.SUPABASE_SERVICE_ROLE_KEY;

  if (!supabaseUrl || !supabaseServiceKey) {
    throw new Error('Supabase URL and Service Role Key must be provided');
  }

  return createSupabaseClient(supabaseUrl, supabaseServiceKey, {
    auth: {
      persistSession: false,
      autoRefreshToken: false,
      detectSessionInUrl: false,
    },
  });
};

/**
 * Verify a Supabase auth token and get the user
 * This can be used to verify auth tokens from client requests
 */
export const verifyAuthToken = async (token: string) => {
  const supabase = createClient();

  const { data, error } = await supabase.auth.getUser(token);

  if (error) {
    return { error: error.message };
  }

  return { user: data.user };
};

/**
 * Create a new user in Supabase Auth
 * This should only be used in trusted server contexts
 */
export const createAuthUser = async (
  email: string,
  password: string,
  metadata?: Record<string, any>
) => {
  const adminClient = createAdminClient();

  const { data, error } = await adminClient.auth.admin.createUser({
    email,
    password,
    email_confirm: true, // Auto-confirm the email
    user_metadata: metadata || {},
  });

  if (error) {
    return { error: error.message };
  }

  return { user: data.user };
};

/**
 * Delete a user from Supabase Auth
 * This should only be used in trusted server contexts
 */
export const deleteAuthUser = async (userId: string) => {
  const adminClient = createAdminClient();

  const { data, error } = await adminClient.auth.admin.deleteUser(userId);

  if (error) {
    return { error: error.message };
  }

  return { success: true };
};

/**
 * Get a user from Supabase Auth by email
 * This should only be used in trusted server contexts
 */
export const getUserByEmail = async (email: string) => {
  const adminClient = createAdminClient();

  const { data, error } = await adminClient.auth.admin.listUsers({
    filter: {
      email: email,
    },
  });

  if (error) {
    return { error: error.message };
  }

  return { user: data.users[0] || null };
};

/**
 * Update a user's metadata in Supabase Auth
 * This should only be used in trusted server contexts
 */
export const updateUserMetadata = async (
  userId: string,
  metadata: Record<string, any>
) => {
  const adminClient = createAdminClient();

  const { data, error } = await adminClient.auth.admin.updateUserById(userId, {
    user_metadata: metadata,
  });

  if (error) {
    return { error: error.message };
  }

  return { user: data.user };
};

export default {
  createClient,
  createAdminClient,
  verifyAuthToken,
  createAuthUser,
  deleteAuthUser,
  getUserByEmail,
  updateUserMetadata,
};

import { NextResponse } from 'next/server';
import type { NextRequest } from 'next/server';
import { signToken, verifyToken } from '@/lib/auth/session';
// Temporarily disable Supabase session refresh until the package is installed
// import { refreshSession } from '@/lib/supabase/middleware';

const protectedRoutes = ['/dashboard', '/admin'];
const adminRoutes = ['/admin'];

export async function middleware(request: NextRequest) {
  const { pathname } = request.nextUrl;
  const sessionCookie = request.cookies.get('session');

  const isProtectedRoute = protectedRoutes.some(route =>
    pathname.startsWith(route)
  );
  const isAdminRoute = adminRoutes.some(route => pathname.startsWith(route));

  if (isProtectedRoute && !sessionCookie) {
    return NextResponse.redirect(new URL('/sign-in', request.url));
  }

  // Refresh Supabase auth session if needed
  // const supabaseRes = await refreshSession(request);

  // Use the response from Supabase refresh (if any) as our base response
  let res = /* supabaseRes ?? */ NextResponse.next();

  if (sessionCookie) {
    try {
      const parsed = await verifyToken(sessionCookie.value);

      if (isAdminRoute && !parsed.user.isSuperadmin) {
        // Redirect non-admins trying to access admin routes
        return NextResponse.redirect(new URL('/dashboard', request.url));
      }

      // Refresh the session token on GET requests to keep the user logged in
      if (request.method === 'GET') {
        const expiresInOneDay = new Date(Date.now() + 24 * 60 * 60 * 1000);
        res.cookies.set({
          name: 'session',
          value: await signToken({
            ...parsed,
            expires: expiresInOneDay.toISOString(),
          }),
          httpOnly: true,
          secure: true,
          sameSite: 'lax',
          expires: expiresInOneDay,
        });
      }
    } catch (error) {
      console.error('Error updating session:', error);
      // If token verification fails, delete the cookie
      res.cookies.delete('session');
      if (isProtectedRoute) {
        return NextResponse.redirect(new URL('/sign-in', request.url));
      }
    }
  }

  return res;
}

export const config = {
  matcher: ['/((?!api|_next/static|_next/image|favicon.ico).*)'],
};

